import React from 'react';
import { connect } from 'react-redux';
import { sendEmail } from '../redux/email';
import { addSeries, addStyle, addVin, addName, addEmail, addPhone } from '../redux/Form';
import '../assets/scss/personal.scss';

class PersonalForm extends React.Component {
    constructor() {
        super()
        this.state = {
            name: '',
            email: '',
            phone: '',
            submitted: false,
            avgPrice: 0,
            cleanPrice: 0,
            roughPrice: 0,
            lowPrice: 0,
            highPrice: 0
        }
    }
    componentDidMount() {
        this.props.blackValue.used_vehicles.used_vehicle_list.map(list => {
            this.setState({
                avgPrice: list.adjusted_tradein_avg,
                cleanPrice: list.adjusted_tradein_clean,
                roughPrice: list.adjusted_tradein_rough,
            })
        })
    }
    componentDidUpdate() {
        if((this.state.cleanPrice > 0 && this.state.roughPrice > 0 && this.state.avgPrice > 0) && (this.state.lowPrice === 0)) {
            this.getPrices()
        }
    }
    getPrices = () => {

    }
    handleChange = e => {
        e.preventDefault();
        const { name, value} = e.target;
        this.setState({
            [name]: value
        })
    }
    handleSubmit = (e) => {
        e.preventDefault();
        let state = this.state;
        let form = this.props.form;
        const message = {
            name: state.name,
            from: state.email,
            phone: state.phone,
            year: form.year,
            make: form.make,
            model: form.model,
            series: form.series,
            style: form.style,
            vin: form.vin,
            uvc: form.uvc,
            files: form.files,
            miles: form.miles,
            lowPrice: form.lowPrice,
            highPrice: form.highPrice,
            zip: form.zip,
            condition: form.condition,
        }
        this.clearInputs();
        this.props.sendEmail(message);
    }
    clearInputs = () => {
        this.setState({
            name: '',
            email: '',
            phone: '',
            submitted: true
        })
    }
    render() {
        return (
            <div> 
                {this.state.submitted 
                ? <h1 className="personal-form-header"> Thank you for using Sell it to me! If you have any questions please feel free to reach out using the contact form! We will get back with you within 24 hours to give you an official offer on your vehicle. </h1>
                : 
                <div className="personal-form-holder">
                    <h1 className="personal-form-header"> Enter your contact information so we can send you an official offer. </h1>
                    <form className="personal-form" onSubmit={this.handleSubmit}>
                        <div className="input-holder">
                            <label className="personal-label" htmlFor="name"> *Name </label>
                            <input type="text" 
                                className="input"
                                onChange={this.handleChange}
                                name="name"
                                value={this.state.name}
                                placeholder="Enter Name" />
                        </div>
                        <div className="input-holder">
                            <label className="personal-label" htmlFor="email"> *Email </label>
                            <input type="text"
                            className="input"
                            onChange={this.handleChange}
                            name="email"
                            value={this.state.email}
                            placeholder="Enter Email" />
                        </div>
                        <div className="input-holder">
                            <label className="personal-label" htmlFor="phone"> *Phone </label>
                            <input type="text"
                                name="phone"
                                className="input"
                                onChange={this.handleChange}
                                value={this.state.phone}
                                placeholder="Enter Phone" />
                        </div>
                        <button onClick={this.handleFileNext} className="submit-button"> Submit </button>
                    </form>
                </div>
                }
            </div>
        )
    }
}

export default connect(state => state, { sendEmail, addSeries, addStyle, addVin, addName, addEmail, addPhone })(PersonalForm);
