import axios from 'axios';

const url = "https://specifications.vinaudit.com/v3/selections?key=9NSOAST0U19Z4HH&list=";

function setVehicle(vehicle) {
    return {
        type: "SET_VEHICLE",
        vehicle
    }
}

export function getVehicleYears() {
    return dispatch => {
        axios.get(url + "year&format=json").then(res => {
            dispatch(setVehicle(res.data.selections))
        }).catch(err => {
            console.log(err)
        })
    }
}

export function getVehicleMakes(year) {
    return dispatch => {
        axios.get(url + `make&year=${year}&format=json`).then(res => {
            dispatch({
                type: 'GET_VEHICLE_MAKES',
                vehicle: res.data.selections
            })
        }).catch(err => {
            console.log(err)
        })
    }
}

export function getVehicleModels(year, make) {
    return dispatch => {
        axios.get(url + `models&year=${year}&make=${make}&format=json`).then(res => {
            dispatch({
                type: 'GET_VEHICLE_MODELS',
                vehicle: res.data.selections
            })
        }).catch(err => {
            console.log(err)
        })
    }
}

export function getVehicleTrims(year, make, model) {
    return dispatch => {
        axios.get(url + `trim`)
    }
}

export default function reducer(vehicle = [], action) {
    switch(action.type) {
        case "SET_VEHICLE":
            return action.vehicle
        default: 
            return vehicle
    }
}
