import axios from 'axios';

const url = "https://specifications.vinaudit.com/v3/selections?key=9NSOAST0U19Z4HH&list=";

export function getVehicleTrims(year, make, model) {
    return dispatch => {
        axios.get(url + `trim&year=${year}&make=${make}&model=${model}&format=json`).then(res => {
            dispatch({
                type: 'GET_VEHICLE_TRIMS',
                trims: res.data.selections
            })
        })
    }
}

export default function reducer(trims = [], action) {
    switch(action.type) {
        case "GET_VEHICLE_TRIMS":
            return action.trims
        default: 
            return trims
    }
}
