const initialForm = [
    {
        vin: ''
    }, {
        year: ''
    }, {
        make: ''
    }, {
        model: '',
    }, {
        uvc: '',
    }, {
        miles: '',
    }, {
        condition: '',
    }, {
        zip: '',
    }, {
        files: [],
    }, {
        index: 1,
    }
]

export function addVin(vin) {
    return dispatch => {
        dispatch({
            type: "ADD_VIN",

        })
    }
}

export function addForm(object) {
    return dispatch => {
        dispatch({
            type: "ADD_FORM",
            state: object
        })
    }
}

export default function reducer(state = initialForm, action) {
    switch(action.type) {
        case 'ADD_FORM':
            return [
                ...state, 
                action.state
            ]
        default:
            return state
    }
}
