import React from 'react';
import axios from 'axios';

class File extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            selectedFile: null,
            name: null,
            from: null,

        }
    }
    handleChange = e => {
        const { name, value } = e.target;
        this.setState({
            [name]: value
        })
    }
    handleDrop = e => {
        console.log(e.target.files[0])
        this.setState({
            selectedFile: e.target.files
        })
    }
    handleClick = () => {
        const data = new FormData()
        for(var i = 0; i < this.state.selectedFile.length; i++) {
            data.append('file', this.state.selectedFile[i])
        }
        data.append()
        axios.post("http://localhost:5000/upload", data)
    }
    render() {
        return (
            <div>
                <form>
                <div className="form-row">
                        <input type="text" name="name" className="input" placeholder="NAME*" value={this.state.name} onChange={this.handleChange} />
                    </div> 
                    <div className="form-row">
                        <input type="text" name="from" className="input" placeholder="EMAIL*" value={this.state.from} onChange={this.handleChange} />
                    </div>
                    <div className="form-row">
                        <input type="number" name="phone" className="input" placeholder="PHONE" value={this.state.phone} onChange={this.handleChange} />
                    </div>
                    <div className="form-row">
                        <input type="text" name="description" className="input" placeholder="DESCRIPTION*" value={this.state.description} onChange={this.handleDrop} />
                    </div> 
                    <input type="file" multiple name="file" onChange={this.handleChange}/>
                    <button type="button" onClick={this.handleClick}> Send Files </button>
                </form>
            </div>
        )
    }
}

export default File;
