import React from 'react';
import '../assets/scss/vinForm.scss';

class VinForm extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            vin: '',
            miles: '',
            zip: ''
        }
    }
    handleChange = (e) => {
        const { name, value } = e.target
        this.setState({
            [name]: value
        })
    }
    render() {
        console.log(this.state)
        return (
            <div className="vin-form-wrapper">
                <form className="vin-form" onSubmit={this.props.handleSubmit}>
                    <div className="vin-row">
                        <div className="vin-full-column">
                            <label htmlFor="vin" className="vin-label"> VIN </label>
                            <input 
                                className="vin-input"
                                type="text" 
                                name="vin" 
                                maxLength="17" 
                                placeholder="17 Digit VIN Number" 
                                onChange={this.handleChange} 
                                value={this.state.vin}
                            />
                        </div>

                    </div>
                    <div className="vin-row">
                        <div className="vin-column">
                            <label htmlFor="miles" className="vin-label"> Miles </label>
                            <input 
                                className="vin-input"                        
                                name="miles"
                                type="text"
                                placeholder="Must be a number" 
                                onChange={this.props.handleChange}
                                value={this.state.miles}
                            />
                        </div>
                        <div className="vin-column">
                            <label htmlFor="zip" className="vin-label"> Zip Code </label>
                            <input 
                                className="vin-input"
                                type="text" 
                                placeholder="Must be a 5 digit number" 
                                name="zip"
                                maxLength="5"
                                onChange={this.props.handleChange}
                                value={this.state.zip}
                            />
                        </div>
                    </div>
                    <button className="main-button"> Submit </button>
                </form>
            </div>
        )
    }
}
export default VinForm;
