import axios from 'axios';

const url = "https://marketvalue.vinaudit.com/getmarketvalue.php?key=9NSOAST0U19Z4HH";

function setRequest(vinAudit) {
    return {
        type: 'SET_REQUEST',
        vinAudit
    }
}

export function getMarketByVin(miles, vin) {
    return dispatch => {
        axios.get(url + `&format=json&period=300&mileage=${miles}&vin=${vin}`).then(res => {
            dispatch(setRequest(res))
        }).catch(err => {
            console.log(err)
        })
    }
}

export function getMarketByMake(year, make, model, trim, miles) {
    return dispatch => {
        axios.get(url + `&format=json&period=&mileage=${miles}&year=${year}&make=${make}&model=${model}&trim=${trim}`)
        .then(res => {
            console.log(res);
            dispatch(setRequest(res))
        }).catch(err => {
            console.log(err)
        })
    }
}

export default function reducer(vinAudit = [], action) {
    switch(action.type) {
        case "SET_REQUEST":
            return action.vinAudit
        default: 
            return vinAudit
    }
}
