import React from 'react';
import { connect } from 'react-redux';
import { sendEmail } from '../redux/email';
import { getBlackValue, getBlackVin } from '../redux/BlackValue';
import { addSeries, addStyle, addVin, addName, addEmail, addPhone, addLowPrice, addHighPrice, addIndex } from '../redux/Form';
import '../assets/scss/personal.scss';

class PersonalForm extends React.Component {
    constructor() {
        super()
        this.state = {
            name: '',
            email: '',
            phone: '',
            submitted: false,
            avgPrice: 0,
            cleanPrice: 0,
            roughPrice: 0,
            lowPrice: 0,
            highPrice: 0
        }
    }
    handleNameChange = e => {
        this.props.addName(e.target.value)
    }
    handleEmailChange = e => {
        this.props.addEmail(e.target.value)
    }
    handlePhoneChange = e => {
        this.props.addPhone(e.target.value)
    }
    handleSubmit = (e) => {
        e.preventDefault();
        if(this.props.form.name.length > 0 && this.props.form.email.length > 0 && this.props.form.phone.length > 0) {
            if(this.props.form.vin.length === 17) {
                this.props.getBlackVin(this.props.form.vin)
            } else {
                this.props.getBlackValue(this.props.form.uvc, this.props.form.miles)
            }
            var index = this.props.form.index + 1;
            this.props.addIndex(index);
        }
        // let state = this.state;
        // let form = this.props.form;
        // const message = {
        //     name: state.name,
        //     from: state.email,
        //     phone: state.phone,
        //     year: form.year,
        //     make: form.make,
        //     model: form.model,
        //     series: form.series,
        //     style: form.style,
        //     vin: form.vin,
        //     uvc: form.uvc,
        //     files: form.files,
        //     miles: form.miles,
        //     lowPrice: form.lowPrice,
        //     highPrice: form.highPrice,
        //     zip: form.zip,
        //     condition: form.condition,
        // }
        // this.clearInputs();
        // this.props.sendEmail(message);
    }
    render() {
        return (
            <div> 
                <div className="personal-form-holder">
                    <h1 className="personal-form-header"> Enter your contact information so we can send you an official offer. </h1>
                    <form className="personal-form" onSubmit={this.handleSubmit}>
                        <div className="input-holder">
                            <label className="personal-label" htmlFor="name"> *Name </label>
                            <input type="text" 
                                className="input"
                                onChange={this.handleNameChange}
                                name="name"
                                value={this.props.form.name}
                                placeholder="Enter Name" />
                        </div>
                        <div className="input-holder">
                            <label className="personal-label" htmlFor="email"> *Email </label>
                            <input type="text"
                            className="input"
                            onChange={this.handleEmailChange}
                            name="email"
                            max-length="10"
                            value={this.props.form.email}
                            placeholder="Enter Email" />
                        </div>
                        <div className="input-holder">
                            <label className="personal-label" htmlFor="phone"> *Phone </label>
                            <input type="text"
                                name="phone"
                                className="input"
                                onChange={this.handlePhoneChange}
                                value={this.props.form.phone}
                                placeholder="Enter Phone" />
                        </div>
                        <button onClick={this.handleFileNext} className="submit-button"> Submit </button>
                    </form>
                </div>
            </div>
        )
    }
}

export default connect(state => state, { sendEmail, addSeries, addStyle, addVin, addName, addEmail, addPhone, addLowPrice, addHighPrice, getBlackValue, getBlackVin, addIndex })(PersonalForm);
