const state = {
    vin: '',
    year: '',
    make: '',
    model: '',
    uvc: '',
    miles: '',
    condition: '',
    zip: '',
    files: [],
    index: 1
}

export function addForm(object) {
    return dispatch => {
        dispatch({
            type: "ADD_FORM",
            state: object
        })
    }
}

export default function reducer(state = state, action) {
    switch(action.type) {
        case 'ADD_FORM':
            return [
                ...state,
                action.state
            ]
        default:
            return state
    }
}
