import React from 'react';
import { connect } from 'react-redux';
import { getVehicleYears } from '../redux/getYears'
import { getVehicleMakes } from '../redux/getMakes';
import { getVehicleModels } from '../redux/getModels'
import { getVehicleTrims } from '../redux/getTrims'
import { getMarketByMake, getMarketByVin } from '../redux/vinAudit';
import Years from './Years';
import Makes from './Makes';
import Models from './Models';
import Trims from './Trims';
import Miles from './Miles';
import Condition from './Condition';
import Pricing from './Pricing';
import FileUpload from './FileUpload';
import '../assets/scss/forms.scss';

class Forms extends React.Component {
    constructor() {
        super();
        this.state = {
            isCarSelected: false,
            year: '',
            make: '',
            model: '',
            trim: '',
            miles: '',
            condition: '',
            zip: '',
            files: [],
            gotYear: false,
            gotMake: false,
            gotModel: false,
            gotTrim: false,
            yearDone: false,
            makeDone: false,
            modelDone: false,
            trimDone: false,
            milesDone: false,
            filesDone: false,
            conditionDone: false,
            zipDone: false,
            index: 0
        }
    }
    componentDidMount() {
        this.props.getVehicleYears();
        if(this.props.years.years && !this.state.gotYear) {
            console.log("GOT YEAR")
            this.setState({ gotYear: true })
        }
    }
    componentDidUpdate() {
        if(this.props.years.years && !this.state.gotYear) {
            this.setState({ gotYear: true })
        }
        if(this.props.makes.makes && !this.state.gotMake) {
            this.setState({ gotMake: true })
        }
        if(this.props.models.years && !this.state.gotModel) {
            this.setState({ gotModel: true })
        }
        if(this.props.trims.years && !this.state.gotTrim) {
            this.setState({ gotTrim: true })
        }
    }
    handleCarClick = () => {
        if(!this.state.isCarSelected) {
            this.setState({ isCarSelected: true })
        }
    }
    handleVinClick = () => {
        if(this.state.isCarSelected) {
            this.setState({ isCarSelected: false })
        }
    }
    handleYearChange = e => {
        console.log(e.target.value)
        e.persist();
        this.setState({
            year: e.target.value,
            yearDone: true,
            index: 1
        })
        this.props.getVehicleMakes(e.target.value)
    }
    handleMakeChange = e => {
        console.log(e.target.value)
        e.persist()
        this.setState({
            make: e.target.value,
            makeDone: true,
            index: 2,
        })
        this.props.getVehicleModels(this.state.year, e.target.value)
    }
    handleModelChange = e => {
        console.log(e.target.value)
        e.persist()
        this.setState({
            model: e.target.value,
            modelDone: true,
            index: 3
        })
        this.props.getVehicleTrims(this.state.year, this.state.make, e.target.value)
    }
    handleTrimChange = e => {
        console.log(e.target.value)
        e.persist();
        this.setState({
            trim: e.target.value,
            trimDone: true,
            index: 4
        })
    }
    handleMileChange = e => {
        e.persist();
        const { name, value } = e.target;
        this.setState({
            [name]: value
        })
    }
    handleConditionChange = e => {
        e.persist();
        this.setState({ condition: e.target.value, conditionDone: true, index: 6 })
    }
    handleZipChange = e => {
        e.persist();
        const { value } = e.target;
        this.setState({
            zip: value,
            index: 7
        })
    }
    handleChange = e => {
        e.persist();
        const { name, value } = e.target;
        this.setState({
            [name]: value,
            index: 8
        })
    }

    handleMileNext = e => {
        e.persist();
        this.setState({
            milesDone: true,
            index: 5
        })
        this.props.getMarketByMake(this.state.year, this.state.make, this.state.model, this.state.trim, this.state.miles)
    }

    handleFileNext = e => {
        e.persist();
        this.setState({
            filesDone: true
        })
    }
    showWidget = e => {
        e.preventDefault();
        let widget = window.cloudinary.createUploadWidget({
            cloudName: 'duw9diprk',
            uploadPreset: 'my_preset'
        }, (error, result) => {
            if (!error && result && result.event === "success") { 
                console.log('Done! Here is the image info: ', result.info.secure_url); 
                this.setState({
                    files: [...this.state.files, result.info.secure_url]
                })
              }
        })
        widget.open()
    }
    deleteFile = (url) => {
        var array = this.state.files.filter(file => file !== url)
        this.setState({
            files: array
        })
    }
    headerSelector = () => {
        if(this.state.index === 0) {
            return ( <h1 className="form-header"> Select A Year </h1> )
        } else if(this.state.index === 1) {
            return ( <h1 className="form-header"> Select A Make </h1> )
        } else if(this.state.index === 2) {
            return ( <h1 className="form-header"> Select A Model </h1> )
        } else if(this.state.index === 3) {
            return ( <h1 className="form-header"> Select A Trim </h1> )
        } else if(this.state.index === 4) {
            return ( <h1 className="form-header"> Enter Current Mileage </h1> )
        } else if(this.state.index === 5) {
            return ( <h1 className="form-header"> Enter Cars Condition </h1> )
        } else if(this.state.index === 1) {
            return ( <h1 className="form-header"> Select A  </h1> )
        }
    }

    render() {
        console.log(this.state)
        return (
            <div className="get-offer-page">
                {this.state.filesDone 
                ? <Pricing files={this.state.files} vehicle={this.props.vinAudit.vehicle} miles={this.state.miles} zip={this.state.zip} condition={this.state.condition} /> 
                : 
                <div className="form-container">
                    <div className="top-form-container">
                        {this.headerSelector()}
                    </div>
                    <div className="form">
                        {!this.state.yearDone
                        ? <Years handleYearChange={this.handleYearChange} />
                        : null }
                        {(!this.state.makeDone && this.state.yearDone)
                        ? <Makes handleMakeChange={this.handleMakeChange} />
                        : null }
                        {(!this.state.modelDone && this.state.makeDone && this.state.yearDone)
                        ? <Models handleModelChange={this.handleModelChange} /> 
                        : null }
                        {(!this.state.trimDone && this.state.yearDone && this.state.makeDone && this.state.modelDone)
                        ? <Trims handleTrimChange={this.handleTrimChange} />
                        : null}
                        {(!this.state.milesDone && this.state.trimDone && this.state.yearDone && this.state.makeDone && this.state.modelDone)
                        ? 
                        <div className="input-holder">
                            <Miles handleChange={this.handleChange} {...this.state.miles} />
                            <button onClick={this.handleMileNext} className="next-button"> NEXT </button>
                        </div>
                        : null }
                        {(!this.state.conditionDone && this.state.milesDone && this.state.trimDone && this.state.yearDone && this.state.makeDone && this.state.modelDone)
                        ? <Condition handleConditionChange={this.handleConditionChange} />
                        : null}
                        {(!this.state.filesDone && this.state.conditionDone && this.state.milesDone && this.state.trimDone && this.state.yearDone && this.state.makeDone && this.state.modelDone)
                        ?                     
                        <div className="input-holder">
                            <FileUpload showWidget={this.showWidget} />
                            <div className="thumbnail-container">
                                {this.state.files.map(file => <div className="file-thumbnail-holder" key={file} onClick={() => this.deleteFile(file)}> <img src={file} className="file-thumbnail" alt="uploaded File" /> <i className="fas fa-minus-circle delete-thumbnail"></i> </div>)}
                            </div>
                            <button className="next-button" onClick={this.handleFileNext}> Next </button>
                        </div>
                        : null}
                    </div>
                </div>
                }
            </div>
        )
    }
}

export default connect(state => state, { getVehicleYears, getVehicleMakes, getVehicleModels, getVehicleTrims, getMarketByMake, getMarketByVin })(Forms);
