import React from 'react';
import { connect } from 'react-redux';
import { addVin, addIndex } from '../redux/Form';
import { getVin } from '../redux/BlackValue';
import button from '../assets/images/get-offer-button.svg';
import { Redirect } from 'react-router-dom';

class VinHome extends React.Component {
    constructor() {
        super();
        this.state = {
            vin: '',
            vinSelected: true,
            makeSelected: false,
            continue: false
        }
    }

    handleChange = e => {
        e.preventDefault();
        this.setState({
            vin: e.target.value
        })
    }

    handleSubmit = e => {
        e.preventDefault();
        var vin = this.state.vin
        if(vin.length === 17) {
            this.props.addVin(vin);
            this.props.addIndex(5)
            this.setState({
                continue: true
            })
        } else {
            this.props.handleError()
        }
    }
    handleVinClick = (e) => {
        e.preventDefault();
        this.setState({
            vinSelected: true,
            makeSelected: false
        })
    }
    handleMakeClick = (e) => {
        e.preventDefault()
        this.setState({
            vinSelected: false,
            makeSelected: true
        })
    }

    render() {
        return (
            this.state.continue 
            ? <Redirect to="/get-offer" />
            :
            <div className="home-input-container">
                <div className="select-box">
                    <div className={this.state.vinSelected ? "selected" : "select-holder"} onClick={this.handleVinClick} id="vin-select">
                        VIN
                    </div>
                    <div className={this.state.makeSelected ? "selected" : "select-holder"} onClick={this.handleMakeClick} id="make-select">
                        NO VIN?
                    </div>
                </div>
                <input type="text" maxLength="17"
                    value={this.state.vin} onChange={this.handleChange}
                    name="vin" placeholder="17 digit Vin Number"
                    className="offer-input" /> 
                    <button onClick={this.handleSubmit} className="vin-submit-holder"> <img src={button} className="vin-submit" /> </button>
            </div>
        )
    }
}

export default connect(state => state, { addVin, addIndex, getVin })(VinHome)
