import React from 'react';
import { connect } from 'react-redux';
import { addYear } from '../redux/Form';
import '../assets/scss/offer.scss';

class Year extends React.Component {
    constructor() {
        super()
        this.state = {
            year: ''
        }
    }
    componentDidUpdate() {
        var year = this.state.year
        if(year.length === 4 && this.props.form.year ) {
            this.props.addYear(year);
        }
    }
    handleChange = e => {
        e.preventDefault()
        this.setState({
            year: e.target.value
        })
    }
    render() {
        return (
            <div className="input-container">
                <input type="number" maxLength="4"
                value={this.state.year} onChange={this.handleChange}
                name="year" placeholder="Must be 4 digits"
                className="offer-input" />    
            </div>
        )
    }
}

export default connect(state => state, {addYear})(Year)

