import React from 'react';
import { connect } from 'react-redux';
import { sendEmail } from '../redux/email';

class PersonalForm extends React.Component {
    constructor() {
        super()
        this.state = {
            name: '',
            email: '',
            phone: '',
            submitted: false
        }
    }
    handleChange = e => {
        e.preventDefault();
        const { name, value} = e.target;
        this.setState({
            [name]: value
        })
    }
}
