import axios from 'axios';

const vinUrl ="/vins";

function setVins(vins) {
    return {
        type: "SET_VIN",
        vins
    }
}

export function getVins() {
    return dispatch => {
        axios.get(vinUrl).then(res => {
            dispatch(setVins(res.data))
        }).catch(err => {
            console.log(err)
        })
    }
}

export function addVin(vin) {
    return dispatch => {
        axios.post(vinUrl, vin).then(res => {
            dispatch(setVins(res.data))
        }).catch(err => {
            console.log(err)
        })
    }
}

export default function reducer(vins = [], action) {
    switch(action.type) {
        case "SET_VIN":
            return action.vins
        default: 
            return vins
    }
}
