import React from 'react';
import VinForm from './VinForm';
import CarForm from './CarForm';
import { Redirect } from 'react-router-dom'
import { connect } from 'react-redux';
import { addVin } from '../redux/vins';
import { getMarketByMake } from '../redux/vinAudit';
import '../assets/scss/forms.scss';

class Forms extends React.Component {
    constructor() {
        super();
        this.state = {
            vinInputs: {
                vin: '',
                miles: '',
                zip: ''
            },
            carInputs: {
                year: '',
                make: '',
                model: '',
                trim: '',
                miles: ''
            },
            isCarSelected: false,
            isVinSubmitted: false,
            isCarSubmitted: false
        }
    }
    handleChange = (e) => {
        e.persist();
        const { name, value } = e.target
        this.setState(prevState => {
            return {
                vinInputs: {
                    ...prevState.vinInputs,
                    [name]: value
                },
                ...prevState.carInputs
            }
        })
    }
    handleCarChange = (e) => {
        e.persist();
        const { name, value } = e.target;
        this.setState(prevState => {
            return {
                ...prevState.vinInputs,
                carInputs: {
                    ...prevState.carInputs,
                    [name]: value
                }
            }
        })
    }
    clearCarInputs = () => {
        this.setState({
            vinInputs: {
                vin: '',
                miles: '',
                zip: '',
                condition: ''
            },
            carInputs: {
                year: '',
                make: '',
                model: '',
                trim: '',
                miles: ''
            },
            isCarSubmitted: true
        })

    }
    clearVinInputs = () => {
        this.setState({
            vinInputs: {
                vin: '',
                miles: '',
                zip: '',
                condition: ''
            },
            carInputs: {
                year: '',
                make: '',
                model: '',
                trim: '',
                miles: ''
            },
            isVinSubmitted: true
        })
    }
    handleSubmit = (e) => {
        e.preventDefault();
        console.log(this.state.vinInputs)
        this.clearInputs();
        return (
            <link to="/upload"></link>
        )
    }
    handleVinSubmit = e => {
        e.preventDefault();
        console.log(this.state.vinInputs);
        var vinInfo = {
            vin: this.state.vinInputs.vin,
            miles: this.state.vinInputs.miles,
            zip: this.state.vinInputs.zip,
            condition: this.state.vinInputs.condition
        }
        this.props.addVin(vinInfo);
        this.clearVinInputs();
        return (
            <Redirect to="/upload" />
        )
    }
    handleCarSubmit = e => {
        e.preventDefault();
        console.log(this.state.carInputs);
        var car = this.state.carInputs;
        var year = car.year;
        var make = car.make;
        var model = car.model;
        var trim = car.trim; 
        var miles = car.miles;
        this.props.addCar(carInfo);
        this.clearCarInputs();
        return (
            <Redirect to="/upload" />
        )
    }
    handleCarClick = () => {
        if(!this.state.isCarSelected) {
            this.setState({ isCarSelected: true })
        }
    }
    handleVinClick = () => {
        if(this.state.isCarSelected) {
            this.setState({ isCarSelected: false })
        }
    }
    render() {
        return (
            <div>
                <div className="select-row">
                    <button className={!this.state.isCarSelected ? 'select-button active' : 'select-button'} onClick={this.handleVinClick}> Enter VIN </button>
                    <button className={this.state.isCarSelected ? 'select-button active' : 'select-button'} onClick={this.handleCarClick}> Enter Car Info </button>
                </div>
                {this.state.isCarSelected
                ? 
                    <CarForm 
                        handleChange={this.handleCarChange} 
                        handleSubmit={this.handleCarSubmit} 
                        {...this.state.carInputs}
                    />
                : 
                    <VinForm 
                        handleChange={this.handleChange} 
                        handleSubmit={this.handleVinSubmit} 
                        {...this.state.vinInputs}
                    />
                }
                {this.state.isVinSubmitted || this.state.isCarSubmitted
                ? <Redirect to="/upload" />
                : null }
            </div>
        )
    }
}

export default connect(state => state, { addVin, addCar })(Forms);
