                <a className="option"> 1982 </a>
                <a className="option"> 1981 </a>
                <input 
                    type="number" 
                    maxLength="4"
                    value={this.props.form.year} 
                    onChange={this.handleChange}
                    name="year" 
                    placeholder="Must be 4 digits"
                    className="offer-input" 
                    />    
            </div>
        )
    }
}

export default connect(state => state, { addYear, getBlackMakes, addIndex, emailSent })(Year)
