import React from 'react';
import { connect } from 'react-redux';
import { addCondition, addIndex } from '../redux/Form';

class Condition extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            clean: props.form.condition === "clean",
            average: props.form.condition === "average",
            rough: props.form.condition === "rough"
        }
    }

    componentDidUpdate(prevProps) {
        const form = this.props.form
        if((prevProps.form.condition !== form.condition) && form.condition.length > 0) {
            setTimeout(
                function() {
                    var index = (this.props.form.index + 1);
                    this.props.addIndex(index);
                }.bind(this), 500
            )
        }
    }
    componentWillUnmount() {
        clearTimeout();
    }
    handleClean = () => {
        return this.props.addCondition('clean')
    }
    handleAverage = () => {
        return this.props.addCondition('average')
    }
    handleRough = () => {
        return this.props.addCondition('rough')
    }
    render() {
        console.log(this.state)
        return (
            <div className="form-radio-column">
                <div className="form-radio-row">
                    <div className="radio-label" htmlFor="condition" onClick={() => this.handleClick('clean')}> 
                        <span className={this.props.form.condition === 'clean' ? 'radio-circle radio-circle-active': 'radio-circle'}></span>
                        <input onChange={this.handleClean} className="radio-input" type="radio" value="clean" name="condition" hidden="true" /> 
                        Clean 
                    </div>
                </div>
                <div className="form-radio-row">
                    <label className="radio-label" htmlFor="condition" onClick={() => this.handleClick('average')}> 
                        <span className={this.props.form.condition === 'average' ? 'radio-circle radio-circle-active': 'radio-circle'}></span>
                        <input className="radio-input" onChange={this.handleAverage} type="radio" value="average" name="condition" hidden="true" /> 
                        Average 
                    </label>
                </div>
                <div className="form-radio-row">
                    <label className="radio-label" htmlFor="condition"  onClick={() => this.handleClick('clean')}> 
                        <span className={this.props.form.condition === 'rough' ? 'radio-circle radio-circle-active': 'radio-circle'}></span>
                        <input className="radio-input" onChange={this.handleRough} type="radio" value="rough" name="condition" hidden="true" /> 
                        Rough 
                    </label>
                </div>
            </div>
        )
    }
}

export default connect(state => state, { addCondition, addIndex })(Condition);
