import axios from 'axios';

const carsUrl = '/cars';

function setCars(car) {
    return {
        type: "SET_CARS",
        car
    }
}

export function getCars() {
    return dispatch => {
        axios.get(carsUrl).then(res => {
            dispatch(setCars(res.data));
        })
        .catch(err => {
            console.log(err)
        })
    }
}

export function addCar(car) {
    return dispatch => {
        axios.post(carsUrl, car).then(res => {
            dispatch(setCars(res.data))
        }).catch(err => {
            console.log(err)
        })
    }
}

const initialCars = []

export default function reducer(cars = initialCars, action) {
    switch(action.type) {
        case 'SET_CARS':
            return action.cars
        default: 
            return cars
    }
}
