export function addVin(vin) {
    return dispatch => {
        dispatch({
            type: "ADD_VIN",
            vin: vin
        })
    }
}
export function addYear(year) {
    return dispatch => {
        dispatch({
            type: "ADD_YEAR",
            year: year
        })
    }
}
export function addMake(make) {
    return dispatch => {
        dispatch({
            type: "ADD_MAKE",
            make: make
        })
    }
}
export function addModel(model) {
    return dispatch => {
        dispatch({
            type: "ADD_MODEL",
            model: model
        })
    }
}
export function addUvc(uvc) {
    return dispatch => {
        dispatch({
            type: "ADD_UVC",
            uvc: uvc
        })
    }
}
export function addMiles (miles) {
    return dispatch => {
        dispatch({
            type: 'ADD_MILES',
            miles: miles
        })
    }
}
export function addCondition (condition) {
    return dispatch => {
        dispatch({
            type: 'ADD_CONDITION',
            condition: condition
        })
    }
}
export function addZip (zip) {
    return dispatch => {
        dispatch({
            type: 'ADD_ZIP',
            zip: zip
        })
    }
}
export function addFiles (files) {
    return dispatch => {
        dispatch({
            type: 'ADD_FILES',
            files: files
        })
    }
}
export function addIndex (index) {
    return dispatch => {
        dispatch({
            type: 'ADD_INDEX',
            index: index
        })
    }
}
export function showError(value) {
    return dispatch => {
        dispatch({
            type: 'SHOW_ERROR',
            error: value
        })
    }
}

export function addSeries(series) {
    return dispatch => {
        dispatch({
            type: "ADD_SERIES",
            series: series
        })
    }
}

const initialForm = {
    vin: '',
    year: '',
    make: '',
    model: '',
    uvc: '',
    miles: '',
    condition: '',
    zip: '',
    files: [],
    index: 1,
    error: false
}

export function clearForm() {
    return dispatch => {
        dispatch({ type: 'CLEAR_FORM' })
    }
}

export default function reducer(state = initialForm, action) {
    switch(action.type) {
        case "ADD_VIN":
            return {
                ...state,
                vin: action.vin
            }
        case "ADD_YEAR":
            return {
                ...state,
                year: action.year
            }
        case "ADD_MAKE":
            return {
                ...state,
                make: action.make
            }
        case "ADD_MODEL":
            return {
                ...state,
                model: action.model
            }
        case "ADD_UVC":
            return {
                ...state,
                uvc: action.uvc
            }
        case "ADD_MILES":
            return {
                ...state,
                miles: action.miles
            }
        case "ADD_CONDITION":
            return {
                ...state,
                condition: action.condition
            }
        case "ADD_ZIP":
            return {
                ...state,
                zip: action.zip
            }
        case "ADD_FILES":
            return {
                ...state,
                files: action.files
            }
        case "ADD_INDEX": 
            return {
                ...state,
                index: action.index
            }
        case "SHOW_ERROR":
            return {
                ...state,
                error: action.error
            }
        case "CLEAR_FORM":
            return initialForm
        default:
            return state
    }
}
