import React from 'react';
import VinForm from './VinForm';
import CarForm from './CarForm';

class Forms extends React.Component {
    constructor() {
        super();
        this.state = {
            vinInputs: {
                vin: '',
                miles: '',
                zip: ''
            },
            carInputs: {
                year: '',
                make: '',
                model: '',
                trim: '',
                condition: '',
                zip: ''
            },
            isCarSelected: false
        }
    }
    handleChange = (e) => {
        e.persist();
        const { name, value } = e.target
        this.setState(prevState => {
            return {
                vinInputs: {
                    ...prevState.vinInputs,
                    [name]: value
                },
                ...prevState.carInputs
            }
        })
    }
    handleCarChange = (e) => {
        e.persist();
        const { name, value } = e.target;
        this.setState(prevState => {
            return {
                ...prevState.vinInputs,
                carInputs: {
                    ...prevState.carInputs,
                    [name]: value
                }
            }
        })
    }
    clearInputs = () => {
        this.setState({
            vinInputs: {
                vin: '',
                miles: '',
                zip: '',
                condition: ''
            },
            carInputs: {
                year: '',
                make: '',
                model: '',
                trim: '',
                condition: ''
            }
        })
    }
    handleSubmit = (e) => {
        e.preventDefault();
        console.log(this.state.vinInputs)
        this.clearInputs();
    }
    handleCarClick = () => {
        if(!this.state.isCarSelected) {
            this.setState({ isCarSelected: true })
        }
    }
    handleVinClick = () => {
        if(this.state.isCarSelected) {
            this.setState({ isCarSelected: false })
        }
    }
    render() {
        console.log(this.state.vinInputs)
        console.log(this.state.carInputs)
        return (
            <div>
                <div className="row">
                    <button className="select-button" onClick={this.handleVinClick}> Enter VIN </button>
                    <button className="select-button" onClick={this.handleCarClick}> Enter Car Info </button>
                </div>
                {this.state.isCarSelected
                ? 
                    <CarForm 
                        handleChange={this.handleCarChange} 
                        handleSubmit={this.handleSubmit} 
                        {...this.state.vinInputs}
                    />
                : 
                    <VinForm 
                        handleChange={this.handleChange} 
                        handleSubmit={this.handleSubmit} 
                        {...this.state.carInputs}
                    />
                }
            </div>
        )
    }
}

export default Forms;
