import React from 'react';
import { connect } from 'react-redux';
import { sendEmail } from '../redux/email';

class PersonalForm extends React.Component {
    constructor() {
        super()
        this.state = {
            name: '',
            email: '',
            phone: '',
        }
    }
    componentDidMount() {
        let vinAudit = this.props.vinAudit
        this.setState({
            vehicle: vinAudit.vehicle,
            miles: vinAudit.mileage,
            lowPrice: vinAudit.prices.below,
            highPrice: vinAudit.prices.average
        })
    }
    handleChange = e => {
        e.preventDefault();
        const { name, value} = e.target;
        this.setState({
            [name]: value
        })
    }

    handleSubmit = (e) => {
        e.preventDefault();
        let state = this.state;
        const message = {
            name: state.name,
            from: state.email,
            phone: state.phone,
            vehicle: this.props.vehicle,
            files: this.props.files,
            miles: this.props.miles,
            lowPrice: this.props.lowPrice,
            highPrice: this.props.highPrice,
            zip: this.props.zip,
            condition: this.props.condition,
        }
        this.props.sendEmail(message);
    }
    render() {
        console.log("PERSONALFORM", this.props)
        console.log(this.state)
        return (
            <div>
                <form onSubmit={this.handleSubmit}>
                    <div className="input-holder">
                        <input type="text" 
                            className="input"
                            onChange={this.handleChange}
                            name="name"
                            value={this.state.name}
                            placeholder="Enter Name" />
                    </div>
                    <div className="input-holder">
                        <input type="text"
                        className="input"
                        onChange={this.handleChange}
                        name="email"
                        value={this.state.email}
                        placeholder="Enter Email" />
                    </div>
                    <div>
                        <input type="text"
                            name="phone"
                            className="input"
                            onChange={this.handleChange}
                            value={this.state.phone}
                            placeholder="Enter Phone" />
                    </div>
                    <button onClick={this.handleFileNext} className="next-button"> Submit </button>
                </form>

            </div>
        )
    }
}

export default connect(state => state, { sendEmail })(PersonalForm);
