import axios from 'axios';

const url = "https://service.blackbookcloud.com/UsedCarWs/UsedCarWs/UsedVehicle/"

export function getMakes(year) {
    return dispatch => {
        axios.get(url + 'Drilldown/' + encodeURIComponent("ALL") + '/' + encodeURIComponent(year) + '?drilldeep=' + encodeURIComponent('false') + '&getclass=' + encodeURIComponent("false") + "&customerid=" + encodeURIComponent('false')).then(res => {
            dispatch({
                type: 'GET_MAKES',
                value: res.data
            })
        }).catch(err => {
            console.log(err)
        })
    }
}

export function getValue(uvc, miles) {
    return dispatch => {
        axios.get(url + "UVC/" + encodeURIComponent(uvc) + '?mileage=' + encodeURIComponent(miles) + '&customerid=' + encodeURIComponent("test")).then(res => {
            console.log('GET_BLACK_VALUE: ', res.data)
            dispatch({
                type: "GET_VALUE",
                value: res.data
            })
        }).catch(err => {
            console.log(err)
        })
    }
}

export function getBlackVin(vin, miles) {
    return dispatch => {
        axios.get(url + encodeURIComponent("VIN") + '/' + encodeURIComponent(vin) + '?mileage=' + encodeURIComponent(miles) + '&customerid=' + encodeURIComponent("test")).then(res => {
            dispatch({
                type: 'GET_BLACK_VIN',
                blackValue: res.data
            })
        }).catch(err => {
            console.log(err)
        })
    }
}

export default function reducer(blackValue = [], action) {
    switch(action.type) {
        case "GET_VALUE":
            return action.blackValue
        case "GET_BLACK_VIN":
            return action.blackValue
        case "GET_MAKES":
            return action.blackValue
        default:
            return blackValue
    }
}
