import React from 'react';
import { connect } from 'react-redux'

class Pricing extends React.Component {
    constructor() {
        super()
        this.state = {
            lowPrice: '',
            highPrice: '',
            failed: false
        }
    }
    componentDidMount() {
        if(this.props.vinAudit.success) {
            var lowPrice = (this.props.vinAudit.prices.below * .70);
            var highPrice = (this.props.vinAudit.prices.average * .80);
            lowPrice = lowPrice.toFixed(2);
            highPrice = highPrice.toFixed(2);
            this.setState({
                lowPrice: lowPrice,
                highPrice: highPrice
            })
        }
        else {
            this.setState({
                failed: true
            })
        }
    }

    render() {
        console.log(this.props)
        return (
            <div>
                {this.state.failed 
                ? <div>
                    <h1> We are unable to give you an instant offer. Please continue and we will send you an official offer within 24 hours. </h1>
                </div>
                :
                <div>
                    <div className="price-container">

                    </div>
                    <h1 className="price-header"> {this.props.vinAudit.vehicle} </h1>
                    <div className="range"> ${this.state.lowPrice} - ${this.state.highPrice} </div>  
                </div>                 
                }


            </div>
        )
    }
}

export default connect(state => state)(Pricing)
