import React from 'react';
import { connect } from 'react-redux';
import { sendEmail } from '../redux/email';
import { getBlackValue, getBlackVin } from '../redux/BlackValue';
import { addSeries, addStyle, addVin, addName, addEmail, addPhone, addLowPrice, addHighPrice } from '../redux/Form';
import '../assets/scss/personal.scss';

class PersonalForm extends React.Component {
    constructor() {
        super()
        this.state = {
            name: '',
            email: '',
            phone: '',
            submitted: false,
            avgPrice: 0,
            cleanPrice: 0,
            roughPrice: 0,
            lowPrice: 0,
            highPrice: 0
        }
    }
    componentDidMount() {
        this.props.blackValue.used_vehicles.used_vehicle_list.map(list => {
            this.setState({
                avgPrice: list.adjusted_tradein_avg,
                cleanPrice: list.adjusted_tradein_clean,
                roughPrice: list.adjusted_tradein_rough,
            })
        })
    }
    componentDidUpdate() {
        if((this.state.cleanPrice > 0 && this.state.roughPrice > 0 && this.state.avgPrice > 0) && (this.state.lowPrice === 0)) {
            this.getPrices()
        }
    }
    getPrices = () => {
        var avgPrice = (this.state.avgPrice - 800);
        var cleanPrice = (this.state.cleanPrice - 800);
        var roughPrice = (this.state.roughPrice - 800);
        var condition = this.props.form.condition;
        let filesLength = this.props.form.files.length;
        var low;
        var high;
        if(condition === 'average') {
            if(filesLength > 0) {
                low = (avgPrice * .70)
                high = (avgPrice * .80)
            } else {
                low = (avgPrice * .80)
                high = (avgPrice * .90)
            }
            this.props.addLowPrice(low)
            this.props.addHighPrice(high)
        } else if(condition === 'clean') {
            if(filesLength > 0) {
                low = (cleanPrice * .70)
                high = (cleanPrice * .80)
            } else {
                low = (cleanPrice * .70)
                high = (cleanPrice * .80)
            }
            this.props.addLowPrice(low)
            this.props.addHighPrice(high)
        } else {
            if(filesLength > 0) {
                low = (roughPrice * .80)
                high = (roughPrice * .90)
            } else {
                low = (roughPrice * .70)
                high = (roughPrice * .80)
            }
            this.props.addLowPrice(low)
            this.props.addHighPrice(high)
        }
    }
    handleNameChange = e => {
        this.props.addName(e.target.value)
    }
    handleEmailChange = e => {
        this.props.addEmail(e.target.value)
    }
    handlePhoneChange = e => {
        this.props.addPhone(e.target.value)
    }
    handleSubmit = (e) => {
        e.preventDefault();
        if(this.props.form.name.length > 0 && this.props.form.email.length > 0 && this.props.form.phone.length > 0) {
            if(this.props.form.vin.length === 17) {
                this.props.getBlackVin(this.props.form.vin)
            }
            this.props.getBlackValue()
        }
        let state = this.state;
        let form = this.props.form;
        const message = {
            name: state.name,
            from: state.email,
            phone: state.phone,
            year: form.year,
            make: form.make,
            model: form.model,
            series: form.series,
            style: form.style,
            vin: form.vin,
            uvc: form.uvc,
            files: form.files,
            miles: form.miles,
            lowPrice: form.lowPrice,
            highPrice: form.highPrice,
            zip: form.zip,
            condition: form.condition,
        }
        this.clearInputs();
        this.props.sendEmail(message);
    }
    clearInputs = () => {
        this.setState({
            name: '',
            email: '',
            phone: '',
            submitted: true
        })
    }
    render() {
        return (
            <div> 
                {this.state.submitted 
                ? <h1 className="personal-form-header"> Thank you for using Sell it to me! If you have any questions please feel free to reach out using the contact form! We will get back with you within 24 hours to give you an official offer on your vehicle. </h1>
                : 
                <div className="personal-form-holder">
                    <h1 className="personal-form-header"> Enter your contact information so we can send you an official offer. </h1>
                    <form className="personal-form" onSubmit={this.handleSubmit}>
                        <div className="input-holder">
                            <label className="personal-label" htmlFor="name"> *Name </label>
                            <input type="text" 
                                className="input"
                                onChange={this.handleNameChange}
                                name="name"
                                value={this.props.form.name}
                                placeholder="Enter Name" />
                        </div>
                        <div className="input-holder">
                            <label className="personal-label" htmlFor="email"> *Email </label>
                            <input type="text"
                            className="input"
                            onChange={this.handleEmailChange}
                            name="email"
                            value={this.props.form.email}
                            placeholder="Enter Email" />
                        </div>
                        <div className="input-holder">
                            <label className="personal-label" htmlFor="phone"> *Phone </label>
                            <input type="text"
                                name="phone"
                                className="input"
                                onChange={this.handlePhoneChange}
                                value={this.props.form.phone}
                                placeholder="Enter Phone" />
                        </div>
                        <button onClick={this.handleFileNext} className="submit-button"> Submit </button>
                    </form>
                </div>
                }
            </div>
        )
    }
}

export default connect(state => state, { sendEmail, addSeries, addStyle, addVin, addName, addEmail, addPhone, addLowPrice, addHighPrice, getBlackValue, getBlackVin })(PersonalForm);
