import React from 'react';
import { connect } from 'react-redux';
import PersonalForm from './PersonalForm'

class Price extends React.Component {
    constructor() {
        super()
        this.state = {
            avgPrice: 0,
            cleanPrice: 0,
            roughPrice: 0,
            lowPrice: 0,
            highPrice: 0,
            failed: false,
            isClicked: false
        }
    }
    componentDidMount() {
        if(this.props.usedVin) {
            this.props.blackVin.used_vehicles.used_vehicle_list.map(list => {
                this.setState({
                    avgPrice: list.adjusted_tradein_avg,
                    cleanPrice: list.adjusted_tradin_clean,
                    roughPrice: list.adjusted_tradein_rough
                })
            })
        } else {
            this.props.blackValue.used_vehicles.used_vehicles_list.map(list => {
                this.setState({
                    avgPrice: list.adjusted_tradein_avg,
                    cleanPrice: list.adjusted_tradin_clean,
                    roughPrice: list.adjusted_tradein_rough
                })
            })
        }
        if(this.props.condition === 'average') {
            let low = (this.state.avgPrice * .7)
            let high = (this.state.avgPrice * .9)
            this.setState({
                lowPrice: low,
                highPrice: high
            })
        } else if(this.props.condition === 'clean') {
            let low = (this.state.cleanPrice * .7)
            let high = (this.state.cleanPrice * .9)
            this.setState({
                lowPrice: low,
                highPrice: high
            })
        } else {
            let low = (this.state.roughPrice * .7)
            let high = (this.state.roughPrice * .9)
            this.setState({
                lowPrice: low,
                highPrice: high
            })
        }
    }
    handleClick = () => {
        this.setState({
            isClicked: true
        })
    }
    render() {
        return (
            <div>
                {this.state.isClicked 
                ? <PersonalForm files={this.props.files} miles={this.props.miles} condition={this.props.condition} zip={this.props.zip} uvc={this.props.uvc} year={this.props.year} make={this.props.make} />
                : 
                <div className="pricing-page">
                    <div className="price-container">
                        <h2 className="price-vehicle"> {this.props.year} {this.props.make} {this.props.model} </h2>
                        <h2 className="price-range"> ${this.state.lowPrice} - ${this.state.highPrice} </h2>
                        <p className="price-text"> If you decide to continue we will send you a final offer within 24 hours. The price can go up or down from the range. If you accept the offer we will come to you and pick up the vehicle and you'll get paid!  </p>
                        <button className="price-button" onClick={this.handleClick}> Continue <i id="price-arrow" className="fas fa-arrow-circle-right"></i> </button>
                    </div>
                </div>
                }
            </div>
        )
    }
}

export default connect(state => state)(Price)
