import React from 'react';
import { connect } from 'react-redux';
import { Redirect } from 'react-router-dom';
import { getMarketByVin } from '../redux/vinAudit';
import '../assets/scss/vinForm.scss';

class VinForm extends React.Component {
    constructor() {
        super()
        this.state = {
            vin: '',
            miles: '',
            isSubmitted: false
        }
    }
    handleSubmit = (e) => {
        e.preventDefault();
        let vin = this.state.vin;
        let miles = this.state.miles;
        this.props.getMarketByVin(miles, vin);
        this.clearInputs();
        this.setState({
            isSubmitted: true
        })
    }
    handleChange = (e) => {
        e.persist();
        const { name, value } = e.target;
        this.setState({
            [name]: value
        })
    }
    clearInputs = () => {
        this.setState({
            vin: '',
            miles: ''
        })
    }
    render() {
        return (
            <div className="vin-form-wrapper">
                <form className="vin-form" onSubmit={this.handleSubmit}>
                    <div className="vin-row">
                        <div className="vin-full-column">
                            <label htmlFor="vin" className="vin-label"> VIN </label>
                            <input 
                                onChange={this.handleChange} 
                                value={this.state.vin}
                                name="vin" 
                                type="text" 
                                placeholder="17 Digit VIN Number" 
                                className="vin-input"
                                maxLength="17" />
                        </div>
                    </div>
                    <div className="vin-row">
                        <div className="vin-full-column">
                            <label htmlFor="miles" className="vin-label"> Miles </label>
                            <input 
                                onChange={this.handleChange}
                                value={this.state.miles}
                                name="miles"
                                type="text"
                                placeholder="Must be a number" 
                                className="vin-input"/>
                        </div>
                    </div>
                    <button className="vin-button"> Submit </button>
                </form>
                {this.state.isSubmitted 
                ? <Redirect to="/uploads" />
                : null}
            </div>
        )
    }
}

export default connect(state => state, { getMarketByVin })(VinForm);
