import React from 'react';
import { connect } from 'react-redux';
import { getVehicleYears } from '../redux/getYears'
import { getVehicleMakes } from '../redux/getMakes';
import { getVehicleModels } from '../redux/getModels'
import { getVehicleTrims } from '../redux/getTrims'
import '../assets/scss/carForm.scss';

class CarForm extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            year: '',
            make: '',
            model: '',
            trim: '',
            miles: '',
            gotYear: false,
            gotMake: false,
            gotModel: false
        }
    }
    componentDidMount() {
        this.props.getVehicleYears();
    }
    componentDidUpdate() {
        if(this.props.years.years && !this.state.gotYear) {
            this.setState({ gotYear: true })
        }
        if(this.props.makes.makes && !this.state.gotMake) {
            this.setState({ gotMake: true })
        }
        if(this.props.models.models && !this.state.gotModel) {
            this.setState({ gotModel: true })
        }
    }
    handleYearChange = e => {
        e.persist();
        const { name, value } = e.target;
        this.setState({
            [name]: value
        })
        this.props.getVehicleMakes(value)
    }
    handleMakeChange = e => {
        e.persist();
        const { name, value } = e.target;
        this.setState({
            [name]: value
        })
        this.props.getVehicleModels(this.state.year, value);
    }
    getYear = () => {
        if(this.props.years.years && this.state.year === '') {
            return (
                this.props.years.years.map(year => (
                    <option key={year.id} name={year.name}> {year.name} </option>
                ))
            )
        }
    }
    getMake = () => {
        if(this.props.makes.makes && this.state.makes === '') {
            return (
                this.props.makes.makes.map(make => (
                    <option key={make.id} name={make.name}> {make.name} </option>
                ))
            )
        }
    }
    render() {
        console.log(this.props)
        return (
            <div>
                <form onSubmit={this.props.handleSubmit}>
                    <div>
                        <select name="year" value={this.state.year} onChange={this.handleYearChange}>
                            <option name="Select"> Select Year </option>
                            {this.getYear()}
                        </select>
                        <select name="make" value={this.state.make} onChange={this.handleMakeChange}>
                            <option name="Select"> Select Make </option>
                            {this.state.gotYear
                            ?  this.props.makes.makes.map(make => (
                                <option key={make.id} name={make.name}> {make.name} </option>
                            ))
                            : null}
                        </select>
                    </div>
                </form>
            </div>
        )
    }
}

export default connect(state => state, { getVehicleYears, getVehicleMakes, getVehicleModels, getVehicleTrims })(CarForm)

