import React from 'react';
import { connect } from 'react-redux';
import { Redirect } from 'react-router-dom';
import PersonalForm from './PersonalForm';
import '../assets/scss/pricing.scss';

class Pricing extends React.Component {
    constructor() {
        super()
        this.state = {
            lowPrice: '',
            highPrice: '',
            failed: false,
            isClicked: false
        }
    }
    componentDidMount() {
        if(this.props.vinAudit.success) {
            var lowPrice = (this.props.vinAudit.prices.below * .70);
            var highPrice = (this.props.vinAudit.prices.average * .80);
            lowPrice = lowPrice.toFixed(2);
            highPrice = highPrice.toFixed(2);
            this.setState({
                lowPrice: lowPrice,
                highPrice: highPrice
            })
        }
        else {
            this.setState({
                failed: true
            })
        }
    }
    handleClick = () => {
        this.setState({
            isClicked: true
        })
    }

    render() {
        console.log(this.props)
        console.log(this.state)
        return (
            <div>
                {this.state.isClicked
                    ? <PersonalForm files={this.props.files} vehicle={this.props.vehicle} miles={this.props.miles} lowPrice={this.props.lowPrice} highPrice={this.props.highPrice} zip={this.props.zip} condition={this.state.condition} />
                :
                <div className="pricing-page">
                    {this.state.failed 
                    ? <div className="price-container">
                        <h2 className="price-vehicle"> {this.props.vinAudit.vehicle} </h2>

                        <p className="price-text"> We are unable to give you an instant offer. Please continue and we will send you a final offer within 24 hours. </p>
                        <button className="price-button" onClick={this.handleClick}> Continue <i id="price-arrow" className="fas fa-arrow-circle-right"></i> </button>
                    </div>
                    :
                    <div>
                        <div className="price-container">
                            <h2 className="price-vehicle"> {this.props.vinAudit.vehicle} </h2>
                            <p className="price-text"> Your estimated offer is: </p>
                            <h2 className="price-range"> ${this.state.lowPrice} - ${this.state.highPrice} </h2>
                            <p className="price-text">  
                                If you decide to continue we will send you a final offer within 24 hours. The price can go up or down from the range. If you accept the offer we will come to you and pick up the vehicle and you'll get paid! 
                            </p>
                            <button className="price-button" onClick={this.handleClick}> Continue <i id="price-arrow" className="fas fa-arrow-circle-right"></i> </button>
                        </div>
                    </div>                 
                    }
                </div>
                }
            </div>
        )
    }
}

export default connect(state => state)(Pricing)
