import React from 'react';
import { connect } from 'react-redux';
import { addCondition } from '../redux/Form';

class Condition extends React.Component {
    constructor() {
        super();
        this.state = {
            clean: false,
            average: false,
            rough: false
        }
    }
    componentDidMount() {
        const form = this.props.form;
        if(form.condition === 'clean') {
            this.setState({ clean: true, average: false, rough: false })
        } else if (form.condition === 'average') {
            this.setState({ clean: false, average: true, rough: false })
        } else if (form.condition === 'rough') {
            this.setState({ clean: true, average: false, rough: false })
        }
    }
    handleChange = (e) => {
        e.preventDefault();
        this.props.addCondition(e.target.value)
    }
    handleClean = e => {
        e.preventDefault();
        this.setState({ clean: true, average: false, rough: false })
        this.props.addCondition('clean')
    }
    handleAverage = e => {
        e.preventDefault();
        this.setState({ average: true, clean: false, rough: false })
        this.props.addCondition('average')
    }
    handleRough = e => {
        e.preventDefault()
        this.setState({ rough: true, average: false, clean: false })
        this.props.addCondition('rough')
    }
    render() {
        return (
            <div className="form-radio-column">
                <div className="form-radio-row">
                    <label className="radio-label" htmlFor="condition"> 
                        <input onSelect={this.handleClean} className="radio-input" type="radio" value="clean" name="condition" /> 
                        Clean 
                    </label>
                </div>
                <div className="form-radio-row">
                    <label className="radio-label" htmlFor="condition"> 
                        <input className="radio-input" onSelect={this.handleAverage} type="radio" value="average" name="condition" /> 
                        Average 
                    </label>
                </div>
                <div className="form-radio-row">
                    <label className="radio-label" htmlFor="condition"> 
                        <input className="radio-input" onSelect={this.handleRough} type="radio" value="rough" name="condition"/> 
                        Rough 
                    </label>
                </div>
            </div>
        )
    }
}

export default connect(state => state, { addCondition })(Condition);
