import React from 'react';
import VinForm from './VinForm';
import CarForm from './CarForm';

class Forms extends React.Component {
    constructor() {
        super();
        this.state = {
            carInputs: {
                zip: '',
                make: '',
                model: '',
                year: '',
                trim: '',
                engine: '',
                trans: '',
                drivetrain: '',
                miles: '',
                condition: ''
            },
            isCarSelected: false
        }
    }
    handleChange = (e) => {
        e.persist();
        this.setState(prevState => {
            return {
                inputs: {
                    ...prevState.inputs,
                    [e.target.name]: e.target.value
                }
            }
        })
    }
    clearInputs = () => {
        this.setState({
            carInputs: {
                zip: '',
                make: '',
                model: '',
                year: '',
                trim: '',
                engine: '',
                trans: '',
                drivetrain: '',
                miles: '',
                condition: ''
            }
        })
    }
    handleSubmit = (e) => {
        this.clearInputs();
    }
    handleCarClick = () => {
        if(!this.state.isCarSelected) {
            this.setState({ isCarSelected: true })
        }
    }
    handleVinClick = () => {
        if(this.state.isCarSelected) {
            this.setState({ isCarSelected: false })
        }
    }
    render() {
        console.log(this.state)
        return (
            <div>
                <div className="row">
                    <button className="select-button" onClick={this.handleVinClick}> Enter VIN </button>
                    <button className="select-button" onClick={this.handleCarClick}> Enter Car Info </button>
                </div>
                {this.state.isCarSelected
                ? 
                    <CarForm 
                        handleSubmit={this.handleSubmit} 
                    />
                : 
                    <VinForm 
                        handleChange={this.handleChange} 
                        handleSubmit={this.handleSubmit} 
                        {...this.state.carInputs}
                    />
                }
            </div>
        )
    }
}

export default Forms;
