import React from 'react';
import { connect } from 'react-redux';
import { sendEmail } from '../redux/email';
import { addIndex, addYear, addMake, addModel, addUvc, addStyle, addSeries, addVin, addLowPrice, addHighPrice } from '../redux/Form';
import '../assets/scss/price.scss';

class Price extends React.Component {
    constructor() {
        super()
        this.state = {
            avgPrice: 0,
            cleanPrice: 0,
            roughPrice: 0,
            lowPrice: 0,
            highPrice: 0,
            year: '',
            make: '',
            model: '',
            series: '',
            style: '',
            uvc: '',
            vin: '',
            failed: false,
            isClicked: false,
            isSent: false
        }
    }
    componentDidMount() {
        console.log("PRICING ", this.props.blackValue)
        this.props.blackValue.used_vehicles.used_vehicle_list.map(list => {
            this.setState({
                avgPrice: list.adjusted_tradein_avg,
                cleanPrice: list.adjusted_tradein_clean,
                roughPrice: list.adjusted_tradein_rough,
                make: list.make,
                year: list.model_year,
                model: list.model,
                series: list.series,
                style: list.style,
                uvc: list.uvc,
                vin: list.vin
            })
            this.getPrice();
        })
        if(this.props.usedVin) {
            this.props.blackVin.used_vehicles.used_vehicle_list.map(list => {
                this.setState({
                    avgPrice: list.adjusted_tradein_avg,
                    cleanPrice: list.adjusted_tradein_clean,
                    roughPrice: list.adjusted_tradein_rough,
                    make: list.make,
                    year: list.model_year,
                    model: list.model,
                    series: list.series,
                    style: list.style,
                    uvc: list.uvc,
                    vin: list.vin
                })
            })
        } else {
            this.props.blackValue.used_vehicles.used_vehicle_list.map(list => {
                this.setState({
                    avgPrice: list.adjusted_tradein_avg,
                    cleanPrice: list.adjusted_tradein_clean,
                    roughPrice: list.adjusted_tradein_rough,
                    make: list.make,
                    year: list.model_year,
                    model: list.model,
                    series: list.series,
                    style: list.style,
                    uvc: list.uvc,
                    vin: list.vin
                })
            })
        }
    }
    componentDidUpdate() {
        if(!this.state.isSent) {
            let props = this.props
            let form = props.form
            let state = this.state
            var message = {
                lowPrice: state.lowPrice,
                highPrice: state.highPrice,
                name: form.name,
                from: form.email,
                phone: form.phone,
                year: state.year,
                make: state.make,
                model: state.model,
                series: state.series,
                style: state.style,
                uvc: state.uvc,
                vin: state.vin,
                zip: form.zip,
                condition: form.condition,
                files: form.files,
                miles: form.miles
            }
            this.props.sendEmail(message);
            this.setState({
                isSent: true
            })
        }
    }
    getPrice = () => {

    }

    handleClick = () => {
        var index = (this.props.form.index + 1)
        this.props.addIndex(index);
    }
    render() {
        console.log(this.state)
        return (
            <div>
                {this.state.isSent 
                ? 
                <div className="pricing-page">
                    <div className="price-container">
                        <h2 className="price-vehicle"> {this.state.year} {this.state.make} {this.state.model} </h2>
                        <h2 className="price-range"> ${this.state.lowPrice} - ${this.state.highPrice} </h2>
                        <p className="price-text"> If you decide to continue we will send you a final offer within 24 hours. The price can go up or down from the range. If you accept the offer we will come to you and pick up the vehicle and you'll get paid!  </p>
                        <button className="price-button" onClick={this.handleClick}> Continue <i id="price-arrow" className="fas fa-arrow-circle-right"></i> </button>
                    </div>
                </div>
                : null }
            </div>
        )
    }
}

export default connect(state => state, { sendEmail, addIndex, addYear, addMake, addModel, addUvc, addStyle, addSeries, addVin, addLowPrice, addHighPrice })(Price)
