import React from 'react';
import { connect } from 'react-redux';
import { getVehicleYears } from '../redux/getYears'
import { getVehicleMakes } from '../redux/getMakes';
import { getVehicleModels } from '../redux/getModels'
import { getVehicleTrims } from '../redux/getTrims'
import '../assets/scss/carForm.scss';

class CarForm extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            year: '',
            make: '',
            model: '',
            trim: '',
            miles: '',
            gotYear: false,
            gotMake: false,
            gotModel: false,
            gotTrim: false
        }
    }
    componentDidMount() {
        this.props.getVehicleYears();
    }
    componentDidUpdate() {
        if(this.props.years.years && !this.state.gotYear) {
            this.setState({ gotYear: true })
        }
        if(this.props.makes.makes && !this.state.gotMake) {
            this.setState({ gotMake: true })
        }
        if(this.props.models.years && !this.state.gotModel) {
            this.setState({ gotModel: true })
        }
        if(this.props.trims.years && !this.state.gotTrim) {
            this.setState({ gotTrim: true })
        }
    }
    handleYearChange = e => {
        e.persist();
        const { name, value } = e.target;
        this.setState({
            [name]: value
        })
        this.props.getVehicleMakes(value)
    }
    handleMakeChange = e => {
        e.persist();
        const { name, value } = e.target;
        this.setState({
            [name]: value
        })
        this.props.getVehicleModels(this.state.year, value);
    }
    handleModelChange = e => {
        e.persist()
        const { name, value } = e.target;
        this.setState({
            [name]: value
        })
        this.props.getVehicleTrims(value);
    }
    handleTrimChange = e => {
        e.persist()
        const { name, value } = e.target;
        this.setState({
            [name]: value
        })
    }
    getYear = () => {
        if(this.props.years.years && this.state.year === '') {
            return (
                this.props.years.years.map(year => (
                    <option key={year.id} name={year.name}> {year.name} </option>
                ))
            )
        }
    }
    render() {
        console.log(this.state)
        return (
            <div>
                <form onSubmit={this.props.handleSubmit}>
                    <div>
                        <select name="year" value={this.state.year} onChange={this.handleYearChange}>
                            <option name="Select"> Select Year </option>
                            {this.state.gotYear 
                            ? this.props.years.years.map(year => (
                                <option key={year.id} name={year.name}> {year.name} </option>
                            ))
                            : null}
                        </select>
                        <select name="make" value={this.state.make} onChange={this.handleMakeChange}>
                            <option name="Select"> Select Make </option>
                            {this.state.gotMake
                            ?  this.props.makes.makes.map(make => (
                                <option key={make.id} name={make.name}> {make.name} </option>
                            ))
                            : null}
                        </select>
                        <select name="model" value={this.state.model} onChange={this.handleModelChange}>
                            <option name="Select"> Select Model </option>
                            {this.state.gotModel 
                            ? this.props.models.years.map(year => year.makes.map(make => make.models.map(model => (
                                <option key={model.id} name={model.id} value={model.id}> {model.name} </option>
                            ))))
                            : null}
                        </select>
                        <select name="trim" value={this.state.trim} onChange={this.handleTrimChange}>
                            <option name="Select"> Select Trim </option>
                            {this.state.gotTrim
                            ? this.props.trims.years.map(year => year.makes.map(make => make.models.map(model => model.trims.map(trim => (
                                <option key={trim.id} name={trim.id} value={trim.id}> {trim.name} </option>
                            )))))
                            : null}
                        </select>
                        <input 
                            type="text"
                            onChange={this.props.handleChange}
                            value={this.state.miles}
                            name="miles"
                            placeholder="Must be a number"

                        />
                    </div>
                </form>
            </div>
        )
    }
}

export default connect(state => state, { getVehicleYears, getVehicleMakes, getVehicleModels, getVehicleTrims })(CarForm)

