import React from 'react';
import { connect } from 'react-redux';
import { sendEmail, purgeEmail } from '../redux/email';

class PersonalForm extends React.Component {
    constructor() {
        super()
        this.state = {
            name: '',
            email: '',
            phone: '',
            files: [],
            vehicle: '',
            miles: '',
            lowPrice: '',
            highPrice: '',
            vin: '',
            zip: '',
            condition: ''
        }
    }
    componentDidMount() {
        let vinAudit = this.props.vinAudit
        this.setState({
            vehicle: vinAudit.vehicle,
            miles: vinAudit.mileage,
            lowPrice: vinAudit.prices.below,
            highPrice: vinAudit.prices.average
        })
    }
    showWidget = e => {
        e.preventDefault();
        let widget = window.cloudinary.createUploadWidget({
            cloudName: 'duw9diprk',
            uploadPreset: 'my_preset'
        }, (error, result) => {
            if (!error && result && result.event === "success") { 
                console.log('Done! Here is the image info: ', result.info.secure_url); 
                this.setState({
                    files: [...this.state.files, result.info.secure_url]
                })
              }
        })
        widget.open()
    }
    deleteFile = (url) => {
        var array = this.state.files.filter(file => file !== url)
        this.setState({
            files: array
        })
    }
    handleSubmit = (e) => {
        e.preventDefault();
        let state = this.state;
        const message = {
            vehicle: state.vehicle,
            miles: state.miles,
            lowPrice: state.lowPrice,
            highPrice: state.highPrice
        }
        this.props.sendEmail(message);
        return this.props.purgeEmail();
    }
    render() {
        console.log(this.state)
        return (
            <div>

            </div>
        )
    }
}

export default connect(state => state, { sendEmail, purgeEmail })(PersonalForm);
