import React from 'react';
import { connect } from 'react-redux';
import { addIndex } from '../redux/Form';
import '../assets/scss/price.scss';

class Price extends React.Component {
    constructor() {
        super()
        this.state = {
            avgPrice: 0,
            cleanPrice: 0,
            roughPrice: 0,
            lowPrice: 0,
            highPrice: 0,
            year: '',
            make: '',
            model: '',
            series: '',
            style: '',
            uvc: '',
            vin: '',
            failed: false,
            isClicked: false
        }
    }
    componentDidMount() {
        if(this.props.usedVin) {
            this.props.blackVin.used_vehicles.used_vehicle_list.map(list => {
                this.setState({
                    avgPrice: list.adjusted_tradein_avg,
                    cleanPrice: list.adjusted_tradein_clean,
                    roughPrice: list.adjusted_tradein_rough,
                    make: this.make,
                    year: list.model_year,
                    model: list.model,
                    series: list.series,
                    style: list.style,
                    uvc: list.uvc,
                    vin: list.vin
                })
            })
        } else {
            this.props.blackValue.used_vehicles.used_vehicle_list.map(list => {
                this.setState({
                    avgPrice: list.adjusted_tradein_avg,
                    cleanPrice: list.adjusted_tradein_clean,
                    roughPrice: list.adjusted_tradein_rough,
                    make: list.make,
                    year: list.model_year,
                    model: list.model,
                    series: list.series,
                    style: list.style,
                    uvc: list.uvc,
                    vin: list.vin
                })
            })
        }
    }
    componentDidUpdate() {
        if((this.state.cleanPrice > 0 && this.state.roughPrice > 0 && this.state.avgPrice > 0) && (this.state.lowPrice === 0)) {
            this.getPrices()
        }
    }
    getPrices = () => {
        if(this.props.condition === 'average') {
            var low
            var high
            if(this.props.form.files.length > 0) {
                low = (this.state.avgPrice * .70)
                high = (this.state.avgPrice * .80)
            } else {
                low = (this.state.avgPrice * .80)
                high = (this.state.avgPrice * .90)
            }
            this.setState({ lowPrice: low, highPrice: high })
        } else if(this.props.condition === 'clean') {
            let low = (this.state.cleanPrice * .80)
            let high = (this.state.cleanPrice * .90)
            if(this.props.form.files.length > 0) {
                low = (this.state.cleanPrice * .70)
                high = (this.state.cleanPrice * .80)
            } else {
                low = (this.state.cleanPrice * .70)
                high = (this.state.avgPrice * .80)
            }
            console.log("LOW:", low)
            console.log("HIGH: ", high)
            this.setState({
                lowPrice: low,
                highPrice: high
            })
        } else {
            let low = (this.state.roughPrice * .80)
            let high = (this.state.roughPrice * .90)
            this.setState({
                lowPrice: low,
                highPrice: high
            })
        }
    }
    handleClick = () => {
        var index = (this.props.form.index + 1)
        this.props.addIndex(index);
    }
    render() {
        console.log(this.state)
        return (
            <div>
                <div className="pricing-page">
                    <div className="price-container">
                        <h2 className="price-vehicle"> {this.props.year} {this.props.make} {this.props.model} </h2>
                        <h2 className="price-range"> ${this.state.lowPrice} - ${this.state.highPrice} </h2>
                        <p className="price-text"> If you decide to continue we will send you a final offer within 24 hours. The price can go up or down from the range. If you accept the offer we will come to you and pick up the vehicle and you'll get paid!  </p>
                        <button className="price-button" onClick={this.handleClick}> Continue <i id="price-arrow" className="fas fa-arrow-circle-right"></i> </button>
                    </div>
                </div>
            </div>
        )
    }
}

export default connect(state => state, { addIndex })(Price)
