import React from 'react';
import VinForm from './VinForm';
import CarForm from './CarForm';
import { Redirect } from 'react-router-dom'
import { connect } from 'react-redux';
import { getMarketByMake, getMarketByVin } from '../redux/vinAudit';
import '../assets/scss/forms.scss';

class Forms extends React.Component {
    constructor() {
        super();
        this.state = {
            vinInputs: {
                vin: '',
                miles: '',
                zip: ''
            },
            isCarSelected: false,
            isVinSubmitted: false,
            isCarSubmitted: false
        }
    }

    handleChange = (e) => {
        e.persist();
        const { name, value } = e.target
        this.setState(prevState => {
            return {
                vinInputs: {
                    ...prevState.vinInputs,
                    [name]: value
                },
                ...prevState.carInputs
            }
        })
    }

    clearInputs = () => {
        this.setState({
            vinInputs: {
                vin: '',
                miles: '',
                zip: '',
                condition: ''
            },
            isCarSubmitted: true
        })
    }
    handleVinSubmit = e => {
        e.preventDefault();
        console.log(this.state.vinInputs);
        const car = this.state.vinInputs;
        var vin = car.vin;
        var miles = car.miles;
        this.props.getMarketByVin(miles, vin);
        this.clearInputs();
    }

    handleCarClick = () => {
        if(!this.state.isCarSelected) {
            this.setState({ isCarSelected: true })
        }
    }
    handleVinClick = () => {
        if(this.state.isCarSelected) {
            this.setState({ isCarSelected: false })
        }
    }

    render() {
        return (
            <div>
                <div className="select-row">
                    <button className={!this.state.isCarSelected ? 'select-button active' : 'select-button'} onClick={this.handleVinClick}> Enter VIN </button>
                    <button className={this.state.isCarSelected ? 'select-button active' : 'select-button'} onClick={this.handleCarClick}> Enter Car Info </button>
                </div>
                {this.state.isCarSelected
                ? 
                    <CarForm />
                : 
                    <VinForm />
                }
                {this.state.isVinSubmitted || this.state.isCarSubmitted
                ? <Redirect to="/upload" />
                : null }
            </div>
        )
    }
}

export default connect(state => state, { getMarketByVin, getMarketByMake})(Forms);
