import React from 'react';
import { sendEmail } from '../redux/email';
import { connect } from 'react-redux';
import { Redirect } from 'react-router-dom'
import '../assets/scss/contactForm.scss';

class ContactForm extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            files: [],
            fileNames: [],
            name: '',
            email: '',
            phone: '',
            options: '',
            condition: '',
            repairs: '',
            negatives: '',
            payoff: '',
            price: '',
            certainty: '',
            vehicle: '',
            mileage: '',
            isSubmitted: false
        }
    }
    componentDidMount() {
        if(this.props.vinAudit.success) { 
            this.setState({
                price: this.props.vinAudit.prices.below,
                certainty: this.props.vinAudit.certainty,
                vehicle: this.props.vinAudit.vehicle,
                mileage: this.props.vinAudit.mileage
            })
        } else {
            this.setState({
                vehicle: this.props.vinAudit.vehicle,
                price: 'Not Found',
                mileage: this.props.vinAudit.mileage,
                certainty: 'Not Found'
            })
        }

    }
    clearInputs = () => {
        this.setState({
            files: [],
            name: '',
            email: '',
            phone: '',
            options: '',
            condition: '',
            repairs: '',
            negatives: '',
            payoff: ''
        })
    }
    showWidget = e => {
        e.preventDefault();
        let widget = window.cloudinary.createUploadWidget({
            cloudName: 'duw9diprk',
            uploadPreset: 'my_preset'
        }, (error, result) => {
            if (!error && result && result.event === "success") { 
                console.log('Done! Here is the image info: ', result.info.secure_url); 
                this.setState({
                    files: [...this.state.files, result.info.secure_url]
                })
              }
        })
        widget.open()
    }
    deleteFile = (url) => {
        var array = this.state.files.filter(file => file !== url)
        this.setState({
            files: array
        })
    }
    handleSubmit = e => {
        e.preventDefault();
        var lowPrice = (this.props.vinAudit.prices.below * .70);
        var highPrice = (this.props.vinAudit.prices.average * .80);
        let email = {
            name: this.state.name,
            from: this.state.email,
            phone: this.state.phone,
            options: this.state.options,
            condition: this.state.condition,
            repairs: this.state.repairs,
            negatives: this.state.negatives,
            payoff: this.state.payoff,
            vehicle: this.props.vinAudit.vehicle,
            miles: this.props.vinAudit.mileage,
            count: this.props.vinAudit.count,
            lowPrice: lowPrice,
            highPrice: highPrice,
            certainty: this.props.vinAudit.certainty,
            files: this.state.files
        }
        this.props.sendEmail(email);
        this.clearInputs();
        this.setState({ isSubmitted: true })
    }
    handleChange = e => {
        e.preventDefault();
        const { name, value } = e.target
        this.setState({
            [name]: value
        })
    }
    render() {
        return (
            <div id="contact-form-page">
            <div className="car-info-box">
                <h2 className="car-type"> {this.props.vinAudit.vehicle} </h2>
                <p className="car-text"> <strong> Miles: </strong> {this.props.vinAudit.mileage} </p>
                <button className="change-button"> Change Vehicle </button>
            </div>
            <form className="upload-form" onSubmit={this.handleSubmit} method="POST">
                <div className="form-row">
                    <div className="form-column">
                        <div className="input-container">
                            <h4 className="car-header4"> Does your car have a payoff with a bank or lender? </h4>
                            <div className="radio-holder">
                                <input type="radio" name="payoff" value="Yes" onChange={this.handleChange} />
                                <label htmlFor="payoff" className="form-label"> Yes </label>
                            </div>
                            <div className="radio-holder">
                                <input type="radio" name="payoff" value="No" onChange={this.handleChange} />
                                <label htmlFor="payoff" className="form-label"> No </label>
                            </div>
                        </div>
                    </div>
                    <div className="form-column">
                        <div class="input-container">
                            <h4 className="car-header4"> Does your vehicle need any repairs? </h4>
                            <div className="radio-holder">
                                <input type="radio" name="repairs" value="Yes" onChange={this.handleChange} />
                                <label htmlFor="repairs" className="form-label"> Yes </label>
                            </div>
                            <div className="radio-holder">
                                <input type="radio" name="repairs" value="no" onChange={this.handleChange} />
                                <label htmlFor="repairs" className="form-label"> No </label>
                            </div>
                        </div>
                    </div>
                </div>
                <div className="form-row">
                    <div className="form-column">
                        <h4 class="car-header4"> What Is Your Vehicle's Condition? </h4>
                        <div className="radio-holder">

                        </div>
                    </div>
                    <div className="form-column">

                    </div>
                </div>

                    <div className="vehicle-condition-box">
                        <label htmlFor="condition" className="form-label">Excellent:</label>
                        <input type="radio" name="condition" value="excellent" onChange={this.handleChange} />
                        <label htmlFor="condition" className="form-label">Very Good:</label>
                        <input type="radio" name="condition" value="very good" onChange={this.handleChange} />
                        <label htmlFor="condition" className="form-label">Good:</label>
                        <input type="radio" name="condition" value="good" onChange={this.handleChange} />
                        <label htmlFor="condition" className="form-label">Poor:</label>
                        <input type="radio" name="condition" value="poor" onChange={this.handleChange} />

                        <p className="condition-text"> Does the vehicle have any after market options? If so please enter them below </p>
                        <input type="text" className="input" name="options" placeholder="Enter options" value={this.state.options} onChange={this.handleChange} />
                        <p className="condition-text"> Please list any negative conditions (check engine light, tire condition, accidents, hail damage, etc.) </p>
                        <input type="text" className="input" name="negatives" placeholder="Enter any problems we need to know." value={this.state.negatives} onChange={this.handleChange} />
                    </div>
                    <div className="upload-photos-box"> 
                        <h3 className="car-header3"> Upload Photos: </h3>
                        <button className="upload-button" onClick={this.showWidget}> Choose files. </button>
                        <div className="thumbnail-container">
                            {this.state.files.map(file => <div key={file} onClick={() => this.deleteFile(file)}> <img src={file} className="file-thumbnail" alt="uploaded File" /> </div>)}
                        </div>
                    </div>
                    <div className="form-row">
                        <label className="form-label" htmlFor="name"> Name: </label>
                        <input type="text" name="name" className="input" placeholder="NAME*" value={this.state.name} onChange={this.handleChange} />    
                    </div>
                    <div className="form-row">
                        <label className="form-label" htmlFor="email"> Email: </label>
                        <input type="text" name="email" className="input" placeholder="EMAIL*" value={this.state.email} onChange={this.handleChange} />
                    </div>
                    <div className="form-row">
                        <div className="form-column">
                            <label className="form-label" htmlFor="phone"> Phone: </label>
                            <input type="text" name="phone" className="input" placeholder="PHONE NUMBER" value={this.state.phone} onChange={this.handleChange} />
                        </div>
                    </div>
                    <div className="form-row">
                        <button className="submit-button"> Submit </button>
                    </div>
                </form>
                {this.state.isSubmitted
                ? <Redirect to="/pricing" />
                : null}
            </div>
        )
    }
}

export default connect(state => state, { sendEmail })(ContactForm);
