import React from 'react';
import { connect } from 'react-redux';
import { Redirect } from 'react-router-dom';
import { getVehicleYears } from '../redux/getYears'
import { getVehicleMakes } from '../redux/getMakes';
import { getVehicleModels } from '../redux/getModels'
import { getVehicleTrims } from '../redux/getTrims'
import { getMarketByMake, getMarketByVin } from '../redux/vinAudit';
import VinForm from './VinForm';
import CarForm from './CarForm';
import '../assets/scss/forms.scss';

class Forms extends React.Component {
    constructor() {
        super();
        this.state = {
            isCarSelected: false
        }
    }

    handleCarClick = () => {
        if(!this.state.isCarSelected) {
            this.setState({ isCarSelected: true })
        }
    }
    handleVinClick = () => {
        if(this.state.isCarSelected) {
            this.setState({ isCarSelected: false })
        }
    }
    render() {
        return (
            <div>

                <div className="select-row">
                    <button className={!this.state.isCarSelected ? 'select-button active' : 'select-button'} onClick={this.handleVinClick}> Enter VIN </button>
                    <button className={this.state.isCarSelected ? 'select-button active' : 'select-button'} onClick={this.handleCarClick}> Enter By Make </button>
                </div>
                {this.state.isCarSelected
                ? 
                    <CarForm />
                : 
                    <VinForm />
                }
            </div>
        )
    }
}

export default connect(state => state, { getVehicleYears, getVehicleMakes, getVehicleModels, getVehicleTrims, getMarketByMake, getMarketByVin})();
