import React from 'react';
import { connect } from 'react-redux';
import { getBlackVin } from '../redux/BlackVin';
import { getBlackMakes } from '../redux/BlackCar';
import { getBlackModels } from '../redux/BlackModels'
import '../assets/scss/offer.scss'

class Offer extends React.Component {
    constructor() {
        super();
        this.state = {
            vin: '',
            year: '',
            make: '',
            model: '',
            trim: '',
            miles: '',
            condition: '',
            zip: '',
            files: [],
            index: 0,
            offerDone: false
        }
    }
    handlePrev = e => {
        e.persist()
        this.setState({
            index: this.state.index - 1
        })
    }
    handleNext = (target) => {
        if((this.state.index === 0) && (this.state.vin.length === 17)) {
            this.props.getBlackVin(this.state.vin);
            this.setState({
                offerDone: true
            })
        } else if((this.state.index === 1) && (this.state.year.length === 4)) {
            this.props.getBlackMakes(this.state.year);
            this.setState({
                index: this.state.index + 1
            })
        } else if(this.state.index === 2) {
            console.log('getBlackModels: ', this.state.year, target)
            this.props.getBlackModels(this.state.year, target);
            this.setState({
                index: this.state.index + 1
            })
        } else if(this.state.index === 3) {
            console.log('getBlackTrim')
        }
        else {
            this.setState({
                index: this.state.index + 1
            })
        }
    }
    handleOptionClick = e => {
        e.persist();
        console.log('value: ' + e.target.value)
        this.setState({
            make: e.target.value
        })
        return this.handleNext(e.target.value)
    }
    handleModelClick = e => {
        e.persist();
        this.setState({
            model: e.target.value,
        })
        return this.handleNext(e.target.value)
    }
    handleChange = e => {
        e.persist()
        const { name, value } = e.target;
        this.setState({
            [name]: value
        })
    }
    handleNextClick = e => {
        e.persist()
        this.setState({
            index: this.state.index + 1
        })
    }
    deleteFile = (url) => {
        var array = this.state.files.filter(file => file !== url)
        this.setState({
            files: array
        })
    }
    showNext = () => {
        if(this.state.index < 8 && this.state.index !== 0) {
            return ( <button onClick={this.handleNext} className="next-button"> Next </button> )
        } else if (this.state.index === 0) {
            return ( <button onClick={this.handleNext} className="next-button-long"> Don't have a Vin? </button> )
        }
    }
    showPrev = () => {
        if(this.state.index > 1) {
            return ( <button onClick={this.handlePrev} className="prev-button"> Prev </button> )
        } else if (this.state.index === 1) {
            return ( <button onClick={this.handlePrev} className="prev-button"> Enter Cars Vin </button> )
        }
    }
    headerSelector = () => {
        if(this.state.index === 0) {
            return ( <h1 className="form-header"> Enter Cars Vin </h1> )
        } else if(this.state.index === 1) {
            return ( <h1 className="form-header"> Select A Year </h1> )
        } else if(this.state.index === 2) {
            return ( <h1 className="form-header"> Select A Make </h1> )
        } else if(this.state.index === 3) {
            return ( <h1 className="form-header"> Select A Model </h1> )
        } else if(this.state.index === 4) {
            return ( <h1 className="form-header"> Select A Trim </h1> )
        } else if(this.state.index === 5) {
            return ( <h1 className="form-header"> Enter Cars Miles </h1> )
        } else if(this.state.index === 6) {
            return ( <h1 className="form-header"> Select Cars Condition </h1> )
        } else if(this.state.index === 7) {
            return ( <h1 className="form-header"> What's Your Zip Code? </h1> )
        } else if(this.state.index === 8) {
            return ( <h1 className="form-header"> Upload Pictures of Your Car </h1> )
        }
    }
    formPart = () => {
        if(this.state.index === 0) {
            return ( 
                <input type="text"
                maxLength="17"
                value={this.state.vin}
                onChange={this.handleChange}
                name="vin"
                placeholder="17 digit Vin Number"
                className="offer-input" /> 
            )
        } else if(this.state.index === 1) {
            return ( 
                <input type="number"
                maxLength="4"
                value={this.state.year}
                onChange={this.handleChange}
                name="year"
                placeholder="Must be 4 digits"
                className="offer-input"
                />    
            )
        } else if(this.state.index === 2) {
            return (
                this.props.blackCar.drilldown.class_list.map(list => list.year_list.map(year => year.make_list.map(make => (
                    <option onClick={this.handleOptionClick} key={make.name} value={make.name} name="make"> {make.name} </option>
                ))))
            )
        }
    } 
    render() {
        console.log("State: ", this.state)
        console.log("Props: ", this.props)
        return (
            <div className="offer-page">
                <div className="form-container">
                    <div className="top-form-container">
                        {this.headerSelector()}
                    </div>    
                    <div className="form">
                        {this.formPart()}
                        <div className="form-bottom-row">
                            {this.showPrev()}
                            {this.showNext()}
                        </div>
                    </div>
                </div>                
            </div>
        )
    }
}

export default connect(state => state, { getBlackMakes, getBlackVin, getBlackModels })(Offer);
