import React from 'react';
import { connect } from 'react-redux';
import { sendEmail, purgeEmail } from '../redux/email';

class PersonalForm extends React.Component {
    constructor() {
        super()
        this.state = {
            name: '',
            email: '',
            phone: '',
            files: [],
            vehicle: '',
            miles: '',
            lowPrice: '',
            highPrice: '',
            vin: '',
            zip: '',
            condition: ''
        }
    }
    componentDidMount() {
        let vinAudit = this.props.vinAudit
        this.setState({
            vehicle: vinAudit.vehicle,
            miles: vinAudit.mileage,
            lowPrice: vinAudit.prices.below,
            highPrice: vinAudit.prices.average
        })
    }
    handleChange = e => {
        e.preventDefault();
        const { name, value} = e.target;
        this.setState({
            [name]: value
        })
    }

    handleSubmit = (e) => {
        e.preventDefault();
        let state = this.state;
        const message = {
            name: state.name,
            from: state.email,
            phone: state.phone,
            files: state.files,
            vehicle: state.vehicle,
            miles: state.miles,
            lowPrice: state.lowPrice,
            highPrice: state.highPrice
        }
        this.props.sendEmail(message);
        return this.props.purgeEmail();
    }
    render() {
        console.log(this.state)
        return (
            <div>
                <form onSubmit={this.handleSubmit}>
                    <div className="input-holder">
                        <input type="text" 
                            className="input"
                            onChange={this.handleChange}
                            name="name"
                            value={this.state.name}
                            placeholder="Enter Name" />
                    </div>
                    <div className="input-holder">
                        <input type="text"
                        className="input"
                        onChange={this.handleChange}
                        name="email"
                        value={this.state.email}
                        placeholder="Enter Email" />
                    </div>
                    <div>
                        <input type="text"
                            name="phone"
                            className="input"
                            onChange={this.handleChange}
                            value={this.state.phone}
                            placeholder="Enter Phone" />
                    </div>
                    <button onClick={this.handleFileNext} className="next-button"> Submit </button>
                </form>

            </div>
        )
    }
}

export default connect(state => state, { sendEmail, purgeEmail })(PersonalForm);
