import React from 'react';
import { connect } from 'react-redux';
import { getBlackVin } from '../redux/BlackVin';
import { getBlackMakes } from '../redux/BlackCar';
import { getBlackModels } from '../redux/BlackModels';
import { getBlackTrims } from '../redux/BlackTrims';
import { getBlackValue } from '../redux/BlackValue';
import Price from './Price';
import '../assets/scss/offer.scss'

class Offer extends React.Component {
    constructor() {
        super();
        this.state = {
            vin: '',
            year: '',
            make: '',
            model: '',
            uvc: '',
            miles: '',
            condition: '',
            zip: '',
            files: [],
            index: 1,
            offerDone: false,
            usedVin: false,
            vinError: false,
            yearError: false,
            makeError: false,
            modelError: false,
            trimError: false,
            mileError: false,
            conditionError: false,
            zipError: false,
            fileError: false,
            showError: false
        }
    }

    //Go back one form entry
    handlePrev = e => { e.persist(); this.setState({ index: this.state.index - 1 }) }

    //If user submits vin and its 17 digits submit to blackbook and move on the pricing
    handleVinSubmit = () => {
        var vin = this.state.vin;
        if(vin.length === 17) {
            this.props.getBlackVin(this.state.vin);
            this.setState({ offerDone: true, usedVin: true })
        } else { this.setState({ vinError: true, offerDone: false }) }
    }

    //Make sure info entered is correct before user continues.
    handleNext = (target) => {
        if(this.state.index === 1) {
            var year = this.state.year
            if(year.length === 4) {
                this.props.getBlackMakes(this.state.year);
                this.next()
            } else {
                this.setState({ yearError: true, showError: true, index: this.state.index })
            }
        } else if(this.state.index === 2) {
            if(target.length > 0) {
                this.props.getBlackModels(this.state.year, target);
                this.next()
            } else if(this.state.make.length > 0) {
                this.props.getBlackModels(this.state.year, this.state.make)
                this.next()
            }
            else {
                this.setState({ makeError: true, showError: true, index: this.state.index })
            }
        } else if(this.state.index === 3) {
            if(target.length > 0) {
                this.props.getBlackTrims(this.state.year, this.state.make, target);
                this.next()
            } else if(this.state.model.length > 0) {
                this.props.getBlackTrims(this.state.year, this.state.make, this.state.model)
                this.next()
            } else {
                this.setState({ modelError: true, showError: true, index: this.state.index })
            }
        } else if(this.state.index === 4) {
            if(target.length > 0) {
                this.next();
            } else if(this.state.uvc.length > 0) {
                this.next();
            } else {
                this.setState({ trimError: true, showError: true, index: this.state.index })
            }
        }
        else if(this.state.index === 5) {
            if(this.state.miles.length > 0) {
                this.props.getBlackValue(this.state.uvc, this.state.miles)
                this.next();
            } else {
                this.setState({ mileError: true, showError: true, index: this.state.index })
            }
        } else if(this.state.index === 6) {
            if(this.state.condition.length > 0) {
                this.next();
            } else {
                this.setState({ conditionError: true, showError: true, })
            }
        } else if(this.state.index === 7) {
            if(this.state.zip.length === 5) {
                this.next();
            } else {
                this.setState({ zipError: true, showError: true, })
            }
        } else if(this.state.index === 8) {
            if(this.state.files.length > 0 || this.state.fileError) {
                this.setState({ offerDone: true, showError: true, })
            } else {
                this.setState({ fileError: true, showError: true, })
            }
        } 
    }

    //Closes the error if user clicks on it or corrects their form entry
    closeError = () => { if(this.state.showError) { this.setState({ showError: false }) } }

    //Moves user to next input
    next = () => { this.setState({ index: this.state.index + 1, showError: false }) }

    //Sets vehicle make state for option user selects.
    handleOptionClick = e => {
        e.preventDefault();
        this.setState({ make: e.target.value })
        return this.handleNext(e.target.value)
    }

    //Sets vehicle model state for option user selects. 
    handleModelClick = e => {
        e.persist();
        this.setState({ model: e.target.value })
        return this.handleNext(e.target.value)
    }

    //Sets vehicle trim state for option user selects
    handleTrimClick = e => {
        e.persist()
        this.setState({ uvc: e.target.value, index: this.state.index + 1, showError: false })
    }

    //Handle input change
    handleChange = e => {
        e.persist()
        const { name, value } = e.target;
        this.setState({ [name]: value })
    }

    //show next unless they are on the vin input page
    showNext = () => {
        if(this.state.index > 0) {
            return ( <button onClick={this.handleNext} className="next-button"> next <i className="fas fa-arrow-right"></i>   </button> )
        } else if (this.state.index === 0) {
            return ( <button onClick={this.handleNext} className="next-button-long"> no vin? <i className="fas fa-arrow-right"></i> </button> )
        }
    }

    //show prev unless they are on the first page then give them option to enter vin
    showPrev = () => {
        if(this.state.index > 1) {
            return ( <button onClick={this.handlePrev} className="prev-button"> <i className="fas fa-arrow-left"></i> back </button> )
        } else if (this.state.index === 1) {
            return ( <button onClick={this.handlePrev} className="prev-button"> <i className="fas fa-arrow-left"></i> enter vin  </button> )
        }
    }

    //Cloudinary widget for file upload
    showWidget = e => {
        e.preventDefault();
        let widget = window.cloudinary.createUploadWidget({ cloudName: 'duw9diprk', uploadPreset: 'my_preset'}, 
        (error, result) => {
            if (!error && result && result.event === "success") { 
                this.setState({ files: [...this.state.files, result.info.secure_url] })
              }
        })
        widget.open()
    }

    //Deletes file from array in state if user clicks on the file
    deleteFile = (url) => {
        var array = this.state.files.filter(file => file !== url)
        this.setState({ files: array })
    }

    //If user selects next twice without any files uploaded they will continue onto pricing page
    noFiles = () => {
        this.setState({ offerDone: true })
    }

    //Decide what error to throw 
    formError = () => {
        if(this.state.index === 0) {
            return ( <p className="error-text"> Enter Vin To Continue </p> )
        } else if(this.state.index === 1) {
            return ( <p className="error-text"> Enter Year to Continue </p> )
        } else if(this.state.index === 2) {
            return ( <p className="error-text"> Select a Make To Continue </p> )
        } else if (this.state.index === 3) {
            return ( <p className="error-text"> Select a Model To Continue </p> )
        } else if (this.state.index === 4) {
            return ( <p className="error-text"> Select a Trim To Continue </p> )
        } else if (this.state.index === 5) {
            return ( <p className="error-text"> Enter Mileage To Continue </p> )
        } else if (this.state.index === 6) {
            return ( <p className="error-text"> Select a Condition To Continue </p> )
        } else if (this.state.index === 7) {
            return ( <p className="error-text"> Enter A Zip Code To Continue </p> )
        } else if (this.state.index === 8) {
            return ( <p className="error-text"> Pictures help us give you a better offer. </p>  )
        }
    }

    //Show header depending on what index(page) user is on
    headerSelector = () => {
        if(this.state.index === 0) {
            return ( <h1 className="form-header"> Enter Cars Vin </h1> )
        } else if(this.state.index === 1) {
            return ( <h1 className="form-header"> Enter Cars Year </h1> )
        } else if(this.state.index === 2) {
            return ( <h1 className="form-header"> Select A Make </h1> )
        } else if(this.state.index === 3) {
            return ( <h1 className="form-header"> Select A Model </h1> )
        } else if(this.state.index === 4) {
            return ( <h1 className="form-header"> Select A Trim </h1> )
        } else if(this.state.index === 5) {
            return ( <h1 className="form-header"> Enter Cars Miles </h1> )
        } else if(this.state.index === 6) {
            return ( <h1 className="form-header"> Select Cars Condition </h1>)
        } else if(this.state.index === 7) {
            return ( <h1 className="form-header"> Enter Your Zip Code? </h1> )
        } else if(this.state.index === 8) {
            return ( <h1 className="form-header"> Upload Pictures </h1> )
        }
    }

    //Show input depending on what index(page) user is on
    formPart = () => {
        if(this.state.index === 0) {
            return ( 
                <div className="vin-container">
                    <input type="text" maxLength="17"
                    value={this.state.vin} onChange={this.handleChange}
                    name="vin" placeholder="17 digit Vin Number"
                    className="offer-input" /> 
                    <button onClick={this.handleVinSubmit} className="vin-submit-button"> Submit </button>
                </div>
            )
        } else if(this.state.index === 1) {
            return ( 
                <div className="input-container">
                    <input type="number" maxLength="4"
                    value={this.state.year} onChange={this.handleChange}
                    name="year" placeholder="Must be 4 digits"
                    className="offer-input" />    
                </div>
            )
        } else if(this.state.index === 2) {
            //Map through black book results for car makes. When user clicks on make get that makes models and set state.
            return (
                <div className="option-container">
                    {this.props.blackCar.drilldown.class_list.map(list => list.year_list.map(year => year.make_list.map(make => (<option className="option" onClick={this.handleOptionClick} key={make.name} value={make.name} name="make"> {make.name} </option> ))))}
                </div>

            )
        } else if(this.state.index === 3) {
            return (
                <div className="option-container">
                    {this.props.blackModels.drilldown.class_list.map(list => list.year_list.map(year => year.make_list.map(make => make.model_list.map(model => (
                        <option className="option" onClick={this.handleModelClick} key={model.name} value={model.name} name="model"> {model.name} </option>
                    )))))}
                </div>
            )
        } else if(this.state.index === 4) {
            return (
                <div className="option-container">
                    {this.props.blackTrims.drilldown.class_list.map(list => list.year_list.map(year => year.make_list.map(make => make.model_list.map(model => model.series_list.map(series => series.style_list.map(style => (
                        <option className="option" onClick={this.handleTrimClick} key={style.uvc} value={style.uvc} name="trim">  {style.name} {series.name} </option>
                    )))))))}
                </div>
            )
        } else if (this.state.index === 5) {
            return (
                <div className="input-container">
                    <input type="text" placeholder="Must be a number"
                    value={this.state.miles} onChange={this.handleChange}
                    name="miles" className="offer-input" />
                </div>
            )
        } else if (this.state.index === 6) {
            return (
                <div className="form-radio-column">
                    <div className="form-radio-row">
                        <label className="radio-label" htmlFor="condition"> 
                        <input className="radio-input" onChange={this.handleChange} type="radio" value="clean" name="condition" /> Clean </label>
                    </div>
                    <div className="form-radio-row">
                        <label className="radio-label" htmlFor="condition"> 
                        <input className="radio-input" onChange={this.handleChange} type="radio" value="average" name="condition" /> Average </label>
                    </div>
                    <div className="form-radio-row">
                        <label className="radio-label" htmlFor="condition"> 
                        <input className="radio-input" onChange={this.handleChange} type="radio" value="rough" name="condition"/> Rough </label>
                    </div>
                </div>
            )
        } else if (this.state.index === 7) {
            return (
                <div>
                    <input type="number"
                        placeholder="5 Digit Number"
                        value={this.state.zip}
                        onChange={this.handleChange}
                        name="zip"
                        className="offer-input"
                        />
                </div>
            )
        } else if (this.state.index === 8) {
            return (
                <div>
                    <div className="upload-photos-box"> 
                        <button className="upload-button" onClick={this.showWidget}> Choose files </button>
                    </div>
                    <div className="thumbnail-container">
                        {this.state.files.map(file => <div className="file-thumbnail-holder" key={file} onClick={() => this.deleteFile(file)}> <img src={file} className="file-thumbnail" alt="uploaded File" /> <i className="fas fa-minus-circle delete-thumbnail"></i> </div>)}
                    </div>
                </div>
            )
        }
    } 
    render() {
        return (
            <div className="offer-page">
                {this.state.offerDone
                ? <Price files={this.state.files}  miles={this.state.miles} condition={this.state.condition} zip={this.state.zip} usedVin={this.state.usedVin} uvc={this.state.uvc} year={this.state.year} make={this.state.make} model={this.state.model} />
                : 
                <div className="form-container">
                    <div className="top-form-container">
                        {this.headerSelector()}
                    </div>    
                    <div className="form">
                        {this.formPart()}
                    </div>
                    {this.state.showError 
                    ?   <div className="form-error-row" onClick={this.closeError}>
                            {this.formError()}
                            <p className="close-error"> X </p>
                        </div>
                    : null}
                    <div className="form-bottom-row">
                        {this.showPrev()}
                        {this.showNext()}
                    </div>
                </div>         
                }       
            </div>
        )
    }
}

export default connect(state => state, { getBlackMakes, getBlackVin, getBlackModels, getBlackTrims, getBlackValue })(Offer);
