const initialForm = [
    {
        vin: ''
    }, {
        year: ''
    }, {
        make: ''
    }, {
        model: '',
    }, {
        uvc: '',
    }, {
        miles: '',
    }, {
        condition: '',
    }, {
        zip: '',
    }, {
        files: [],
    }, {
        index: 1,
    }
]

export function addVin(vin) {
    return dispatch => {
        dispatch({
            type: "ADD_VIN",
            vin: vin
        })
    }
}
export function addYear(year) {
    return dispatch => {
        dispatch({
            type: "ADD_YEAR",
            year: year
        })
    }
}
export function addMake(make) {
    return dispatch => {
        dispatch({
            type: "ADD_MAKE",
            make: make
        })
    }
}
export function addUvc(uvc) {
    return dispatch => {
        dispatch({
            type: "ADD_UVC",
            uvc: uvc
        })
    }
}
export function addMiles (miles) {
    return dispatch => {
        dispatch({
            type: 'ADD_MILES',
            miles: miles
        })
    }
}
export function addCondition (condition) {
    return dispatch => {
        dispatch({
            type: 'ADD_CONDITION',
            condition: condition
        })
    }
}
export function addZip (zip) {
    return dispatch => {
        dispatch({
            type: 'ADD_ZIP',
            zip: zip
        })
    }
}
export function addFiles (files) {
    return dispatch => {
        dispatch({
            type: 'ADD_FILES',
            files: files
        })
    }
}

export function addForm(object) {
    return dispatch => {
        dispatch({
            type: "ADD_FORM",
            state: object
        })
    }
}

export default function reducer(state = initialForm, action) {
    switch(action.type) {
        case 'ADD_FORM':
            return [
                ...state, 
                action.state
            ]
        default:
            return state
    }
}
