import axios from 'axios';

const url = '/carForm';

function setCarForm(carForm) {
    return {
        type: 'SET_CAR_FORM',
        carForm
    }
}

export function getCarForms() {
    return dispatch => {
        axios.get(url).then(res => {
            dispatch(setCarForm(res.data))
        }).catch(err => {
            console.log(err)
        })
    }
}

export function getCarForm(id) {
    return dispatch => {
        axios.get(url + id).then(res => {
            dispatch(setCarForm(res.data))
        }).catch(err => {
            console.log(err)
        })
    }
}

export function addCarForm(form) {
    return dispatch => {
        axios.post(url, form).then(res => {
            dispatch(setCarForm(res.data))
        }).catch(err => {
            console.log(err)
        })
    }
}

export default function reducer(carForm = [], action) {
    switch(action.type) {
        case 'GET_CAR_FORMS':
            return action.carForm
        case 'GET_CAR_FORM':
            return action.carForm
        case 'ADD_CAR_FORM':
            return action.carForm
        default:
            return carForm
    }
}
