import React from 'react';
import { connect } from 'react-redux';
import { getVehicleYear } from '../redux/getVehicle';
import '../assets/scss/carForm.scss';

class CarForm extends React.Component {
    constructor() {
        super()
        this.state = {
            isLoaded: false,
            year: ''
        }
    }

    componentDidMount() {
        this.props.getVehicleYear();
        if(this.props.vehicle) {
            this.setState({
                isLoaded: true
            })
        }
    }
    getYear = () => {
        if(this.state.isLoaded) {
            return (
                <select name="year" value={this.props.year} onChange={this.props.handleChange}>
                    {this.props.vehicle.years.map(year => (
                        <option key={year.id} value={year.name}>{year.name}</option>
                    ))}
                </select>
            ) 
        }
    }
    render() { 
        console.log(this.props.vehicle)
        return (
            <div>

                    {this.getYear}

                {/* <select name="year" value={this.state.year}>
                    {this.props.vehicle.years.map(year => (
                        <option key={year.id} value={year.name}></option>
                    ))}
                </select> */}

            </div>
        )
    }
}


