import React from 'react';
import { connect } from 'react-redux';
import { Redirect } from 'react-router-dom';
import { getVehicleYears } from '../redux/getYears'
import { getVehicleMakes } from '../redux/getMakes';
import { getVehicleModels } from '../redux/getModels'
import { getVehicleTrims } from '../redux/getTrims'
import { getMarketByMake, getMarketByVin } from '../redux/vinAudit';
import Years from './Years';
import Makes from './Makes';
import Models from './Models';
import Trims from './Trims';
import Miles from './Miles';
// import VinForm from './VinForm';
// import CarForm from './CarForm';
import '../assets/scss/forms.scss';

class Forms extends React.Component {
    constructor() {
        super();
        this.state = {
            isCarSelected: false,
            year: '',
            make: '',
            model: '',
            trim: '',
            miles: '',
            gotYear: false,
            gotMake: false,
            gotModel: false,
            gotTrim: false,
        }
    }
    componentDidMount() {
        this.props.getVehicleYears();
    }
    componentDidUpdate() {
        if(this.props.years.years && !this.state.gotYear) {
            this.setState({ gotYear: true })
        }
        if(this.props.makes.makes && !this.state.gotMake) {
            this.setState({ gotMake: true })
        }
        if(this.props.models.years && !this.state.gotModel) {
            this.setState({ gotModel: true })
        }
        if(this.props.trims.years && !this.state.gotTrim) {
            this.setState({ gotTrim: true })
        }
    }

    handleCarClick = () => {
        if(!this.state.isCarSelected) {
            this.setState({ isCarSelected: true })
        }
    }
    handleVinClick = () => {
        if(this.state.isCarSelected) {
            this.setState({ isCarSelected: false })
        }
    }
    handleYearChange = e => {
        console.log(e.target.value)
        e.persist();
        this.setState({
            year: e.target.value
        })
        this.props.getVehicleMakes(e.target.value)
    }
    handleMakeChange = e => {
        console.log(e.target.value)
        e.persist()
        this.setState({
            make: e.target.value
        })
        this.props.getVehicleModels(this.state.year, e.target.value)
    }
    handleModelChange = e => {
        console.log(e.target.value)
        e.persist()
        this.setState({
            model: e.target.value
        })
        this.props.getVehicleTrims(this.state.year, this.state.make, e.target.value)
    }
    handleTrimChange = e => {
        console.log(e.target.value)
        e.persist();
        this.setState({
            trim: e.target.value
        })
    }
    handleMileChange = e => {
        e.persist();
        const { name, value } = e.target;
        this.setState({
            [name]: value
        })
        this.props.getMarketByMake(this.state.year, this.state.make, this.state.model, this.state.trim, e.target.value)
    }
    render() {
        console.log(this.state)
        return (
            <div>
                <div className="form-container">
                    <div className="top-form-container">
                        <h1 className="form-header"> Select a year </h1>
                    </div>
                    <div className="form">
                        {(this.state.gotYear && (this.state.year === ''))
                        ? <Years handleYearChange={this.handleYearChange} />
                        : null }
                        {(this.state.gotMake && (this.state.make === '') && (this.state.year !== ''))
                        ? <Makes handleMakeChange={this.handleMakeChange} />
                        : null }
                        {(this.state.gotModel && (this.state.model === '') && (this.state.make !== ''))
                        ? <Models handleModelChange={this.handleModelChange} /> 
                        : null }
                        {(this.state.gotTrim && (this.state.trim === '') && (this.state.model !== ''))
                        ? <Trims handleTrimChange={this.handleTrimChange} />
                        : null}
                        {((this.state.miles === '') && this.state.trim !== '')
                        }
                    </div>
                </div>
            </div>
        )
    }
}

export default connect(state => state, { getVehicleYears, getVehicleMakes, getVehicleModels, getVehicleTrims, getMarketByMake, getMarketByVin})(Forms);
