import React from 'react';
import { connect } from 'react-redux';
import { addMake } from '../redux/Form';
import { getBlackModels } from '../redux/BlackModels';

class Make extends React.Component {
    handleClick = e => {
        e.preventDefault();
        this.props.addMake(e.target.value);
        this.props.getBlackModels(this.props.form.year, e.target.value);
        let index = this.props.index + 1
        this.props.addIndex(index)
    }
    render() {
        return (
            <div className="option-container">
                {this.props.blackCar.drilldown.class_list.map(list => list.year_list.map(year => year.make_list.map(make => (<option className="option" onClick={this.handleClick} key={make.name} value={make.name} name="make"> {make.name} </option> ))))}
            </div>
        )
    }
}

export default connect(state => state, { addMake, getBlackModels })(Make);
