import React from 'react';
import axios from 'axios';
import Files from 'react-butterfiles';

class File extends React.Component {
    constructor(props) {
        super(props);
        this.state = {

        }
    }
    handleChange = e => {
        console.log(e.target.files[0])
        this.setState({
            selectedFile: e.target.files
        })
    }
    handleClick = () => {
        const data = new FormData()
        for(var i = 0; i < this.state.selectedFile.length; i++) {
            data.append('file', this.state.selectedFile[i])
        }
        axios.post("http://localhost:5000/upload", data)
    }
    render() {
        return (
            <div>
                <form>
                    {/* <input type="file" multiple name="file" onChange={this.handleChange}/>
                    <button type="button" onClick={this.handleClick}> Send Files </button> */}
                    <Files multiple={true} maxSize="5mb" multipleMaxSize="50mb" accept={["application/pdf","image/jpg","image/jpeg", "image/png"]}onSuccess={files => this.setState({ files })} onError={errors => this.setState({ errors })}>
                        {({browseFiles}) => (
                            <>
                            <button onClick={browseFiles}> Upload Images </button>
                            <ol>
                                {this.state.files.map(file => (
                                    <li key={file.name}> {file.name} </li>
                                ))}
                                {this.state.errors.map(error => (
                                    <li key={error.file.name}>
                                        {error.file.name} - {error.type}
                                    </li>
                                ))}
                            </ol>
                            </>
                        )}
                    </Files>
                </form>
            </div>
        )
    }
}

export default File;
