import React from 'react';
import { Link } from 'react-router-dom';
import './assets/scss/nav.scss';

class Nav extends React.Component {
    constructor() {
        super()
        this.state = {
            isOpen: false
        }
    }
    handleClick = e => {
        e.preventDefault()
        this.setState({ isOpen: !this.state.isOpen })
    }
    closeNav = e => {
        e.preventDefault()
        this.setState({ isOpen: false })
    }
    render() {
        console.log(this.state)
        return (
            <div className="nav-bar-container">
                <div className="nav-top-row">
                    <h1 className="nav-header"> SELL IT TO ME </h1>
                </div>
                <div className="navbar-container">
                    <ul className="navbar">
                        <li className="nav-bars-holder" onClick={this.handleClick}>
                            <i className="fas fa-bars" id="nav-bars"></i>
                        </li>
                        <li onClick={this.closeNav} className={this.state.isOpen ? "nav-link-holder nav-holder" : "nav-link-closed nav-holder"}>
                            <Link className="nav-link" to="/"> Home </Link>
                        </li>
                        <li onClick={this.closeNav} className={this.state.isOpen ? "nav-link-holder nav-holder" : "nav-link-closed nav-holder"}>
                            <Link className="nav-link" to="/get-offer"> Get Offer </Link>
                        </li>
                        <li onClick={this.closeNav} className={this.state.isOpen ? "nav-link-holder" : "nav-link-closed"}>
                            <Link className="nav-link" to="/enter-vin"> How it works </Link>
                        </li>
                        <li onClick={this.closeNav} className={this.state.isOpen ? "nav-link-holder" : "nav-link-closed"}>
                            <Link className="nav-link" to="/contact"> Contact Us </Link>
                        </li>
                    </ul>
                </div>
            </div>
        )
    }
}

export default Nav;
